import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';

const MainCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 600);
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Simulación de imágenes - reemplaza con tus imágenes reales
  const carouselImages = [
    'https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?w=1600',
    'https://images.unsplash.com/photo-1511671782779-c97d3d27a1d4?w=1600',
    'https://images.unsplash.com/photo-1619983081563-430f63602796?w=1600',
    'https://images.unsplash.com/photo-1514320291840-2e0a9bf2a9ae?w=1600',
  ];

  const genres = ['Rock', 'Jazz', 'Pop', 'Soul'];
  const subTitles = [
    'Powerful beats and raw energy',
    'Smooth rhythms and improvisation',
    'Catchy melodies and vibrant tunes',
    'Soulful vocals and emotional depth',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prev) => (prev + 1) % carouselImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [carouselImages.length]);

  const handlePrev = () => {
    setActiveIndex((prev) => (prev - 1 + carouselImages.length) % carouselImages.length);
  };

  const handleNext = () => {
    setActiveIndex((prev) => (prev + 1) % carouselImages.length);
  };

  return (
    <div className="relative w-full overflow-hidden" style={{ height: isMobile ? '500px' : '700px' }}>
      {/* Slides */}
      {carouselImages.map((image, index) => (
        <div
          key={index}
          className={`absolute inset-0 transition-all duration-800 ease-in-out ${
            index === activeIndex ? 'opacity-100 scale-100' : 'opacity-0 scale-105'
          }`}
        >
          {/* Imagen de fondo */}
          <div className="absolute inset-0">
            <img
              src={image}
              alt={`${genres[index]} collection`}
              className="w-full h-full object-cover"
              style={{ filter: 'brightness(0.75) contrast(1.1)' }}
            />
            {/* Overlay gradiente */}
            <div
              className="absolute inset-0"
              style={{
                background: 'linear-gradient(to right, rgba(0,0,0,0.75) 0%, rgba(0,0,0,0.3) 50%, rgba(0,0,0,0.1) 100%)',
              }}
            />
          </div>

          {/* Contenido de texto */}
          <div
            className={`absolute z-10 transition-all duration-800 ${
              index === activeIndex ? 'opacity-100 translate-x-0' : 'opacity-0 -translate-x-8'
            }`}
            style={{
              top: '50%',
              left: isMobile ? '5%' : '8%',
              transform: 'translateY(-50%)',
              maxWidth: isMobile ? '90%' : '500px',
            }}
          >
            {/* Línea decorativa */}
            <div
              className={`h-1 bg-yellow-500 mb-4 transition-all duration-600 ${
                index === activeIndex ? 'w-16' : 'w-0'
              }`}
              style={{ transitionDelay: index === activeIndex ? '200ms' : '0ms' }}
            />

            {/* Título del género */}
            <h1
              className="font-extrabold uppercase mb-4 leading-none tracking-tight"
              style={{
                fontSize: isMobile ? '2.5rem' : '5rem',
                textShadow: '2px 2px 20px rgba(0,0,0,0.5)',
                background: 'linear-gradient(135deg, #FFC709 0%, #FFE066 100%)',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                backgroundClip: 'text',
              }}
            >
              {genres[index]}
            </h1>

            {/* Subtítulo */}
            <p
              className="font-light tracking-wider leading-relaxed"
              style={{
                color: 'rgba(255, 255, 255, 0.9)',
                fontSize: isMobile ? '0.95rem' : '1.25rem',
                letterSpacing: '0.05em',
                textShadow: '1px 1px 10px rgba(0,0,0,0.7)',
                maxWidth: '400px',
              }}
            >
              {subTitles[index]}
            </p>

            {/* Botón CTA */}
            <button
              className="mt-8 px-8 py-3 font-semibold uppercase tracking-widest border-2 border-yellow-500 text-yellow-500 bg-transparent transition-all duration-300 hover:bg-yellow-500 hover:text-black hover:-translate-y-1 hover:shadow-xl"
              style={{
                fontSize: isMobile ? '0.85rem' : '0.95rem',
                boxShadow: 'none',
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.boxShadow = '0 8px 20px rgba(255, 199, 9, 0.3)';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.boxShadow = 'none';
              }}
            >
              Explorar colección
            </button>
          </div>
        </div>
      ))}

      {/* Botones de navegación */}
      <button
        onClick={handlePrev}
        className="absolute z-20 transition-all duration-300 hover:scale-110"
        style={{
          top: '50%',
          left: isMobile ? '10px' : '30px',
          transform: 'translateY(-50%)',
          backgroundColor: 'rgba(0, 0, 0, 0.3)',
          backdropFilter: 'blur(8px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          borderRadius: '50%',
          padding: isMobile ? '8px' : '12px',
          color: 'white',
          cursor: 'pointer',
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.backgroundColor = 'rgba(255, 199, 9, 0.9)';
          e.currentTarget.style.color = '#000';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.backgroundColor = 'rgba(0, 0, 0, 0.3)';
          e.currentTarget.style.color = 'white';
        }}
      >
        <ChevronLeft size={isMobile ? 28 : 36} />
      </button>

      <button
        onClick={handleNext}
        className="absolute z-20 transition-all duration-300 hover:scale-110"
        style={{
          top: '50%',
          right: isMobile ? '10px' : '30px',
          transform: 'translateY(-50%)',
          backgroundColor: 'rgba(0, 0, 0, 0.3)',
          backdropFilter: 'blur(8px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          borderRadius: '50%',
          padding: isMobile ? '8px' : '12px',
          color: 'white',
          cursor: 'pointer',
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.backgroundColor = 'rgba(255, 199, 9, 0.9)';
          e.currentTarget.style.color = '#000';
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.backgroundColor = 'rgba(0, 0, 0, 0.3)';
          e.currentTarget.style.color = 'white';
        }}
      >
        <ChevronRight size={isMobile ? 28 : 36} />
      </button>

      {/* Indicadores personalizados */}
      <div
        className="absolute z-20 flex gap-3"
        style={{
          bottom: isMobile ? '20px' : '40px',
          left: '50%',
          transform: 'translateX(-50%)',
        }}
      >
        {carouselImages.map((_, index) => (
          <div
            key={index}
            onClick={() => setActiveIndex(index)}
            className="cursor-pointer transition-all duration-300 hover:opacity-100"
            style={{
              width: activeIndex === index ? '40px' : '10px',
              height: '10px',
              borderRadius: '5px',
              backgroundColor: activeIndex === index ? '#FFC709' : 'rgba(255, 255, 255, 0.4)',
            }}
          />
        ))}
      </div>
    </div>
  );
};

export default MainCarousel;
