import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography, Button } from "@mui/material";
import { shades } from "../theme";
import { addToCart } from "../state";
import { useNavigate } from "react-router-dom";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles((theme) => ({
  itemContainer: {
    position: "relative",
    transition: "transform 0.3s, box-shadow 0.3s",
    "&:hover": {
      transform: "scale(1.05)",
      boxShadow: theme.shadows[8],
    },
  },
  overlay: {
    display: "flex",
    justifyContent: "space-between",
    position: "absolute",
    bottom: "10%",
    left: "0",
    width: "100%",
    padding: "0 5%",
    backgroundColor: theme.palette.background.paper,
    borderRadius: "3px",
    visibility: "hidden",
  },
  overlayVisible: {
    visibility: "visible",
  },
}));

const Item = ({ item, width }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [isHovered, setIsHovered] = useState(false);
  const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
  const classes = useStyles();

  const { category, price, name, image } = item.attributes;

  const {
    data: {
      attributes: {
        formats: {
          medium: { url },
        },
      },
    },
  } = image;

  return (
    <Box width={width} className={classes.itemContainer}>
      <Box
        position="relative"
        onMouseOver={() => setIsHovered(true)}
        onMouseOut={() => setIsHovered(false)}
      >
        <img
          alt={item.name}
          width="400px"
          height="400px"
          src={url}
          onClick={() => navigate(`/item/${item.id}`)}
          style={{ cursor: "pointer" }}
        />
        <Box
          className={`${classes.overlay} ${
            isHovered ? classes.overlayVisible : ""
          }`}
        >
          <Button
            onClick={() => {
              if (!isItemInCart) {
                dispatch(addToCart({ item: { ...item, count: 1 } }));
              }
            }}
            sx={{
              backgroundColor: isItemInCart ? "#999999" : shades.primary[500],
              color: "white",
              cursor: isItemInCart ? "default" : "pointer",
            }}
            disabled={isItemInCart}
          >
            {isItemInCart ? "In Cart" : "Add to Cart"}
          </Button>
        </Box>
      </Box>

      <Box mt="3px">
        <Typography variant="subtitle2" color={shades.neutral.dark}>
          {category
            .replace(/([A-Z])/g, " $1")
            .replace(/^./, (str) => str.toUpperCase())}
        </Typography>
        <Typography>{name}</Typography>
        <Typography fontWeight="bold">â‚¬ {price}</Typography>
      </Box>
    </Box>
  );
};

export default Item;
