import React from "react";
import { Box, IconButton, useMediaQuery, Typography } from "@mui/material";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import { Link } from "react-router-dom"; // Importamos Link para navegación

const MainCarousel = () => {
  const isNonMobile = useMediaQuery("(min-width:600px)");

  // Definimos la información de las imágenes
  const carouselData = [
    {
      title: "Rock",
      subtitle: "Powerful beats and raw energy",
      image: heroTextureImports["joan_jett.jpg"],
      link: "/rock", // Ruta a la sección de Rock
    },
    {
      title: "Jazz",
      subtitle: "Smooth rhythms and improvisation",
      image: heroTextureImports["john_coltrane.jpg"],
      link: "/jazz", // Ruta a la sección de Jazz
    },
    {
      title: "Pop",
      subtitle: "Catchy melodies and vibrant tunes",
      image: heroTextureImports["madonna.jpg"],
      link: "/pop", // Ruta a la sección de Pop
    },
    {
      title: "Soul",
      subtitle: "Soulful vocals and emotional depth",
      image: heroTextureImports["marvin_gaye.jpg"],
      link: "/soul", // Ruta a la sección de Soul
    },
  ];

  return (
    <Carousel
      infiniteLoop={true}
      showThumbs={false}
      showIndicators={false}
      showStatus={false}
      renderArrowPrev={(onClickHandler, hasPrev, label) => (
        <IconButton
          onClick={onClickHandler}
          sx={{
            position: "absolute",
            top: "50%",
            left: "0",
            color: "#FFC709",
            padding: "5px",
            zIndex: "10",
          }}
        >
          <NavigateBeforeIcon sx={{ fontSize: 40 }} />
        </IconButton>
      )}
      renderArrowNext={(onClickHandler, hasNext, label) => (
        <IconButton
          onClick={onClickHandler}
          sx={{
            position: "absolute",
            top: "50%",
            right: "0",
            color: "#FFC709",
            padding: "5px",
            zIndex: "10",
          }}
        >
          <NavigateNextIcon sx={{ fontSize: 40 }} />
        </IconButton>
      )}
    >
      {carouselData.map((data, index) => (
        <Box key={`carousel-image-${index}`}>
          <img
            src={data.image}
            alt={`carousel-${index}`}
            style={{
              width: "100%",
              height: "700px",
              objectFit: "cover",
              backgroundAttachment: "fixed",
            }}
          />
          <Box
            position="absolute"
            top="50%"
            left="50%"
            transform="translate(-50%, -50%)"
            textAlign="center"
          >
            <Typography variant="h4">{data.title}</Typography>
            <Typography variant="subtitle1">{data.subtitle}</Typography>
            <Link to={data.link}>
              <Typography variant="subtitle2">Explore more</Typography>
            </Link>
          </Box>
        </Box>
      ))}
    </Carousel>
  );
};

export default MainCarousel;
