import React from "react";
import { Box, IconButton, useMediaQuery, Typography } from "@mui/material";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import { Link } from "react-router-dom";
import { heroTextureImports } from "../../assets/logo";

const MainCarousel = () => {
  const isNonMobile = useMediaQuery("(min-width:600px)");

  const carouselImages = Object.values(heroTextureImports);

  const genres = ["Rock", "Jazz", "Pop", "Soul"];
  const subTitles = [
    "Powerful beats and raw energy",
    "Smooth rhythms and improvisation",
    "Catchy melodies and vibrant tunes",
    "Soulful vocals and emotional depth",
  ];

  return (
    <Carousel
      infiniteLoop={true}
      showThumbs={false}
      showIndicators={false}
      showStatus={false}
      renderArrowPrev={(onClickHandler, hasPrev, label) => (
        <IconButton
          onClick={onClickHandler}
          sx={{
            position: "absolute",
            top: "50%",
            left: "0",
            color: "#FFC709",
            padding: "5px",
            zIndex: "10",
          }}
        >
          <NavigateBeforeIcon sx={{ fontSize: 40 }} />
        </IconButton>
      )}
      renderArrowNext={(onClickHandler, hasNext, label) => (
        <IconButton
          onClick={onClickHandler}
          sx={{
            position: "absolute",
            top: "50%",
            right: "0",
            color: "#FFC709",
            padding: "5px",
            zIndex: "10",
          }}
        >
          <NavigateNextIcon sx={{ fontSize: 40 }} />
        </IconButton>
      )}
    >
      {carouselImages.map((texture, index) => (
        <Box key={`carousel-image-${index}`}>
          <img
            src={texture}
            alt={`carousel-${index}`}
            style={{
              width: "100%",
              height: "700px",
              objectFit: "cover",
              backgroundAttachment: "fixed",
            }}
          />
          <Box
            position="absolute"
            top="50%"
            left="50%"
            transform="translate(-50%, -50%)"
            textAlign="center"
          >
            <Typography
              variant="h1"
              sx={{ fontWeight: "bold", textTransform: "uppercase" }}
            >
              {genres[index]}
            </Typography>
            <Typography variant="subtitle1" sx={{ textTransform: "uppercase" }}>
              {subTitles[index]}
            </Typography>
            <Link to={`/${genres[index].toLowerCase()}`}>
              <Typography
                variant="subtitle2"
                sx={{ textTransform: "uppercase" }}
              >
                Explore more
              </Typography>
            </Link>
          </Box>
        </Box>
      ))}
    </Carousel>
  );
};

export default MainCarousel;
