import React, { useState, useCallback, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography, IconButton, Chip } from "@mui/material";
import { Add, Remove, ShoppingBag } from "@mui/icons-material";
import { shades } from "../theme";
import { addToCart, removeFromCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item, style }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [isHovered, setIsHovered] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [touchPosition, setTouchPosition] = useState({ x: 0, y: 0 });

  // Memoized values and handlers
  const handleAddToCart = useCallback((e) => {
    e.stopPropagation();
    if (item?.attributes) {
      dispatch(addToCart({ item: { ...item, count: 1 } }));
    }
  }, [dispatch, item]);

  const handleRemoveFromCart = useCallback((e) => {
    e.stopPropagation();
    if (item?.id) {
      dispatch(removeFromCart({ id: item.id }));
    }
  }, [dispatch, item]);

  const handleNavigate = useCallback(() => {
    if (item?.id) {
      navigate(`/item/${item.id}`);
    }
  }, [navigate, item]);

  const handleMouseMove = useCallback((e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;
    setTouchPosition({ x, y });
  }, []);

  // Extract and memoize item data
  const { itemData, cartItem, imageUrl } = useMemo(() => {
    if (!item?.attributes) {
      return { itemData: null, cartItem: null, imageUrl: "" };
    }

    const { category = "Unknown", price = 0, name = "Unnamed Item", image } = item.attributes;

    const imageUrl = image?.data?.attributes?.formats?.medium?.url 
      ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
      : "/default-image.jpg";

    const cartItem = cart.find((cartItem) => cartItem.id === item.id);

    return {
      itemData: { category, price, name },
      cartItem,
      imageUrl
    };
  }, [item, cart]);

  // Loading/Error placeholder
  if (!item?.attributes) {
    return (
      <Box 
        sx={{ 
          maxWidth: { xs: '100%', sm: '400px' }, 
          margin: '0 auto',
          ...style 
        }}
      >
        <Box
          sx={{
            backgroundColor: shades.neutral[100],
            aspectRatio: '1 / 1',
            width: '100%',
            borderRadius: '0px',
            animation: 'pulse 2s ease-in-out infinite',
            '@keyframes pulse': {
              '0%, 100%': { opacity: 0.8 },
              '50%': { opacity: 0.6 },
            },
          }}
        />
      </Box>
    );
  }

  const { category, price, name } = itemData;
  const isInCart = !!cartItem;
  const itemCount = cartItem?.count || 0;

  // Modern color palette
  const colors = {
    primary: shades.primary[500],
    background: '#f8f8f8',
    text: '#1a1a1a',
    border: '#e5e5e5',
    accent: '#8B4513' // Warm brown for vinyl aesthetic
  };

  const styles = {
    container: {
      position: 'relative',
      maxWidth: { xs: '100%', sm: '400px' },
      margin: '0 auto',
      cursor: 'pointer',
      backgroundColor: colors.background,
      border: `1px solid ${colors.border}`,
      transition: 'all 0.4s cubic-bezier(0.23, 1, 0.32, 1)',
      overflow: 'hidden',
      '&:hover': {
        backgroundColor: 'white',
        transform: 'scale(1.02)',
        borderColor: colors.text,
      },
      ...style
    },
    imageWrapper: {
      position: 'relative',
      aspectRatio: '1 / 1',
      overflow: 'hidden',
      backgroundColor: colors.background,
    },
    image: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
      transition: 'all 0.6s cubic-bezier(0.23, 1, 0.32, 1)',
      opacity: imageLoaded ? 1 : 0,
      filter: isHovered ? 'contrast(1.1) brightness(1.05)' : 'contrast(1) brightness(1)',
      transform: isHovered ? 'scale(1.05)' : 'scale(1)',
    },
    overlay: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: `radial-gradient(circle at ${touchPosition.x}% ${touchPosition.y}%, rgba(255,255,255,0.3) 0%, transparent 50%)`,
      opacity: isHovered ? 1 : 0,
      transition: 'opacity 0.3s ease',
      pointerEvents: 'none',
    },
    categoryChip: {
      position: 'absolute',
      top: '12px',
      left: '12px',
      backgroundColor: 'rgba(255,255,255,0.9)',
      color: colors.text,
      fontSize: '0.7rem',
      fontWeight: 500,
      letterSpacing: '1px',
      padding: '4px 8px',
      borderRadius: '0px',
      backdropFilter: 'blur(10px)',
      border: `1px solid ${colors.border}`,
    },
    cartControls: {
      position: 'absolute',
      bottom: '16px',
      right: '16px',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      backgroundColor: 'rgba(255,255,255,0.9)',
      padding: '4px',
      borderRadius: '0px',
      backdropFilter: 'blur(10px)',
      border: `1px solid ${colors.border}`,
      opacity: isHovered || isInCart ? 1 : 0,
      transform: isHovered || isInCart ? 'translateY(0)' : 'translateY(10px)',
      transition: 'all 0.3s cubic-bezier(0.23, 1, 0.32, 1)',
    },
    controlButton: {
      minWidth: '32px',
      width: '32px',
      height: '32px',
      borderRadius: '0px',
      color: colors.text,
      border: `1px solid ${colors.border}`,
      transition: 'all 0.2s ease',
      '&:hover': {
        backgroundColor: colors.primary,
        color: 'white',
        borderColor: colors.primary,
      },
      '& .MuiSvgIcon-root': {
        fontSize: '1rem',
      }
    },
    quantityDisplay: {
      minWidth: '20px',
      textAlign: 'center',
      fontSize: '0.8rem',
      fontWeight: 600,
      color: colors.text,
    },
    infoPanel: {
      padding: '20px 16px 16px',
      borderTop: `1px solid ${colors.border}`,
    },
    name: {
      fontSize: { xs: '1rem', sm: '1.1rem' },
      fontWeight: 400,
      color: colors.text,
      lineHeight: 1.3,
      letterSpacing: '-0.2px',
      marginBottom: '8px',
      fontFamily: '"Jost", sans-serif',
    },
    priceSection: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    price: {
      fontSize: '1.2rem',
      fontWeight: 300,
      color: colors.text,
      letterSpacing: '-0.5px',
      fontFamily: '"Jost", sans-serif',
    },
    addButton: {
      color: colors.primary,
      fontSize: '0.8rem',
      fontWeight: 500,
      letterSpacing: '0.5px',
      textTransform: 'uppercase',
      padding: '4px 8px',
      border: `1px solid ${colors.primary}`,
      borderRadius: '0px',
      transition: 'all 0.2s ease',
      opacity: isInCart ? 0 : 1,
      '&:hover': {
        backgroundColor: colors.primary,
        color: 'white',
      },
    }
  };

  return (
    <Box
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onMouseMove={handleMouseMove}
      onClick={handleNavigate}
      sx={styles.container}
    >
      {/* Image Section */}
      <Box sx={styles.imageWrapper}>
        <Box
          component="img"
          alt={name}
          src={imageUrl}
          onLoad={() => setImageLoaded(true)}
          sx={styles.image}
        />

        {/* Dynamic Light Overlay */}
        <Box sx={styles.overlay} />

        {/* Category Chip */}
        <Chip 
          label={category.toUpperCase()} 
          sx={styles.categoryChip}
          size="small"
        />

        {/* Cart Controls */}
        <Box sx={styles.cartControls}>
          {isInCart ? (
            <>
              <IconButton 
                onClick={handleRemoveFromCart}
                sx={styles.controlButton}
                size="small"
              >
                <Remove />
              </IconButton>
              <Typography sx={styles.quantityDisplay}>
                {itemCount}
              </Typography>
              <IconButton 
                onClick={handleAddToCart}
                sx={styles.controlButton}
                size="small"
              >
                <Add />
              </IconButton>
            </>
          ) : (
            <IconButton 
              onClick={handleAddToCart}
              sx={styles.controlButton}
              size="small"
            >
              <ShoppingBag />
            </IconButton>
          )}
        </Box>
      </Box>

      {/* Info Section */}
      <Box sx={styles.infoPanel}>
        <Typography sx={styles.name}>
          {name}
        </Typography>

        <Box sx={styles.priceSection}>
          <Typography sx={styles.price}>
            â‚¬{price.toFixed(2)}
          </Typography>

          {!isInCart && (
            <Box 
              component="button"
              onClick={handleAddToCart}
              sx={styles.addButton}
            >
              Add
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default Item;
