import React, { useState, useCallback, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography } from "@mui/material";
import { addToCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item, style }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [imageLoaded, setImageLoaded] = useState(false);

  // Memoized values and handlers
  const handleAddToCart = useCallback((e) => {
    e.stopPropagation();
    if (item?.attributes) {
      const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
      if (!isItemInCart) {
        dispatch(addToCart({ item: { ...item, count: 1 } }));
      }
    }
  }, [cart, dispatch, item]);

  const handleNavigate = useCallback(() => {
    if (item?.id) {
      navigate(`/item/${item.id}`);
    }
  }, [navigate, item]);

  // Extract and memoize item data
  const { itemData, isItemInCart, imageUrl } = useMemo(() => {
    if (!item?.attributes) {
      return { itemData: null, isItemInCart: false, imageUrl: "" };
    }

    const { category = "Unknown", price = 0, name = "Unnamed Item", image } = item.attributes;

    const imageUrl = image?.data?.attributes?.formats?.medium?.url 
      ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
      : "/default-image.jpg";

    const isInCart = cart.some((cartItem) => cartItem.id === item.id);

    return {
      itemData: { category, price, name },
      isItemInCart: isInCart,
      imageUrl
    };
  }, [item, cart]);

  // Loading/Error placeholder
  if (!item?.attributes) {
    return (
      <Box 
        sx={{ 
          maxWidth: { xs: '100%', sm: '400px' }, 
          margin: '0 auto',
          ...style 
        }}
      >
        <Box
          sx={{
            backgroundColor: '#fafafa',
            aspectRatio: '1 / 1',
            width: '100%',
          }}
        />
      </Box>
    );
  }

  const { category, price, name } = itemData;

  // Ultra minimal color palette
  const colors = {
    text: '#000000',
    background: '#ffffff',
    border: '#f0f0f0',
    accent: '#000000'
  };

  const styles = {
    container: {
      maxWidth: { xs: '100%', sm: '400px' },
      margin: '0 auto',
      cursor: 'pointer',
      backgroundColor: colors.background,
      transition: 'opacity 0.3s ease',
      '&:hover': {
        opacity: 0.8,
      },
      ...style
    },
    imageContainer: {
      aspectRatio: '1 / 1',
      backgroundColor: '#fafafa',
      marginBottom: '16px',
      overflow: 'hidden',
    },
    image: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
      opacity: imageLoaded ? 1 : 0,
      transition: 'opacity 0.5s ease',
    },
    category: {
      fontSize: '0.7rem',
      fontWeight: 400,
      color: '#666',
      letterSpacing: '1px',
      textTransform: 'uppercase',
      marginBottom: '4px',
      fontFamily: 'system-ui, sans-serif',
    },
    name: {
      fontSize: '0.9rem',
      fontWeight: 400,
      color: colors.text,
      lineHeight: 1.4,
      marginBottom: '8px',
      fontFamily: 'system-ui, sans-serif',
    },
    priceSection: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
    },
    price: {
      fontSize: '0.9rem',
      fontWeight: 400,
      color: colors.text,
      fontFamily: 'system-ui, sans-serif',
    },
    addButton: {
      color: colors.text,
      fontSize: '0.7rem',
      fontWeight: 400,
      textTransform: 'uppercase',
      letterSpacing: '0.5px',
      padding: '2px 0',
      border: 'none',
      backgroundColor: 'transparent',
      cursor: 'pointer',
      opacity: isItemInCart ? 0 : 1,
      transition: 'opacity 0.2s ease',
      fontFamily: 'system-ui, sans-serif',
      '&:hover': {
        opacity: 0.6,
      },
    }
  };

  return (
    <Box
      onClick={handleNavigate}
      sx={styles.container}
    >
      {/* Image */}
      <Box sx={styles.imageContainer}>
        <Box
          component="img"
          alt={name}
          src={imageUrl}
          onLoad={() => setImageLoaded(true)}
          sx={styles.image}
        />
      </Box>

      {/* Info */}
      <Box>
        <Typography sx={styles.category}>
          {category}
        </Typography>

        <Typography sx={styles.name}>
          {name}
        </Typography>

        <Box sx={styles.priceSection}>
          <Typography sx={styles.price}>
            â‚¬{price.toFixed(2)}
          </Typography>

          {!isItemInCart && (
            <Box 
              component="button"
              onClick={handleAddToCart}
              sx={styles.addButton}
            >
              Add to Cart
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default Item;
