import React, { useEffect, useState, useMemo, useCallback } from "react";
import { 
  Tabs, 
  Tab, 
  Box, 
  Typography, 
  TextField,
  InputAdornment,
  Fade,
  Skeleton,
  Container,
} from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";

const API_URL = "https://sprint9-ecommerce-production.up.railway.app/api/items?populate=image&pagination[limit]=50";

const CATEGORIES = [
  { label: "ALL", value: "all" },
  { label: "JAZZ", value: "jazz" },
  { label: "POP", value: "pop" },
  { label: "ROCK", value: "rock" },
  { label: "SOUL", value: "soul" },
];

const ShoppingList = () => {
  const dispatch = useDispatch();
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const items = useSelector((state) => state.cart.items);

  const handleCategoryChange = useCallback((event, newValue) => {
    setSelectedCategory(newValue);
  }, []);

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value);
  }, []);

  // Filter items
  const filteredItems = useMemo(() => {
    return items.filter(item => {
      const matchesCategory = selectedCategory === "all" || 
        item.attributes?.category?.toLowerCase() === selectedCategory;

      const matchesSearch = searchTerm.trim() === "" ||
        item.attributes?.name?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  }, [items, selectedCategory, searchTerm]);

  // Listen for category changes from MainCarousel
  useEffect(() => {
    const handleExternalCategoryChange = (event) => {
      setSelectedCategory(event.detail.category);
    };

    window.addEventListener('categoryChange', handleExternalCategoryChange);

    const savedCategory = sessionStorage.getItem('selectedCategory');
    if (savedCategory && CATEGORIES.some(cat => cat.value === savedCategory)) {
      setSelectedCategory(savedCategory);
      sessionStorage.removeItem('selectedCategory');
    }

    return () => {
      window.removeEventListener('categoryChange', handleExternalCategoryChange);
    };
  }, []);

  // Fetch items from API
  useEffect(() => {
    const fetchItems = async () => {
      setIsLoading(true);
      try {
        const response = await fetch(API_URL, { 
          method: "GET",
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const itemsData = await response.json();

        if (itemsData.data && Array.isArray(itemsData.data)) {
          dispatch(setItems(itemsData.data));
        } else {
          console.error("Unexpected API response structure:", itemsData);
          dispatch(setItems([]));
        }
      } catch (error) {
        console.error("Error fetching items:", error);
        dispatch(setItems([]));
      } finally {
        setIsLoading(false);
      }
    };

    fetchItems();
  }, [dispatch]);

  // Loading skeleton
  const renderLoadingSkeleton = () => (
    <Box
      sx={{
        display: "grid",
        gridTemplateColumns: {
          xs: "1fr",
          sm: "repeat(2, 1fr)",
          lg: "repeat(3, 1fr)",
        },
        gap: { xs: 2, sm: 3, md: 4 },
        px: { xs: 2, sm: 3, md: 4 },
        py: 4,
      }}
    >
      {[1, 2, 3, 4, 5, 6].map((i) => (
        <Box key={i}>
          <Skeleton 
            variant="rectangular" 
            height={350} 
            sx={{ 
              borderRadius: 2,
              mb: 1,
            }} 
          />
          <Skeleton variant="text" width="80%" />
          <Skeleton variant="text" width="40%" />
        </Box>
      ))}
    </Box>
  );

  // Render content
  const renderContent = () => {
    if (isLoading) {
      return renderLoadingSkeleton();
    }

    if (filteredItems.length === 0) {
      return (
        <Fade in timeout={500}>
          <Box
            sx={{
              textAlign: "center",
              py: 8,
              px: 2,
            }}
          >
            <Typography 
              variant="h5" 
              color="text.secondary"
              sx={{ 
                mb: 1,
                fontWeight: 500,
              }}
            >
              {searchTerm 
                ? "No albums found"
                : "No albums available"
              }
            </Typography>
            <Typography 
              variant="body2" 
              color="text.secondary"
            >
              {searchTerm 
                ? `Try adjusting your search or filter`
                : `Check back soon for new releases`
              }
            </Typography>
          </Box>
        </Fade>
      );
    }

    return (
      <Fade in timeout={500}>
        <Box
          sx={{
            display: "grid",
            gridTemplateColumns: {
              xs: "1fr",
              sm: "repeat(2, 1fr)",
              lg: "repeat(3, 1fr)",
            },
            gap: { xs: 2, sm: 3, md: 4 },
            px: { xs: 2, sm: 3, md: 4 },
            py: 4,
            justifyItems: "center",
            alignItems: "start",
          }}
        >
          {filteredItems.map((item) => (
            <Box
              key={`item-${item.id}-${item.attributes?.name}`}
              sx={{
                width: "100%",
                maxWidth: "450px",
                transition: "transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out",
                "&:hover": {
                  transform: "translateY(-8px)",
                },
              }}
            >
              <Item item={item} />
            </Box>
          ))}
        </Box>
      </Fade>
    );
  };

  return (
    <Box 
      component="section"
      aria-label="Music albums collection"
      id="shopping-list"
      sx={{
        width: "100%",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        pt: { xs: 6, sm: 8, md: 10 },
        pb: { xs: 6, sm: 8, md: 10 },
      }}
    >
      <Container 
        maxWidth="xl"
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        {/* Header */}
        <Typography 
          variant="h3"
          component="h2"
          textAlign="center"
          sx={{ 
            mb: { xs: 3, sm: 4 },
            fontWeight: 700,
            fontSize: { xs: "2rem", sm: "2.5rem", md: "3rem" },
            letterSpacing: "-0.02em",
          }}
        >
          Our Featured{" "}
          <Box 
            component="span" 
            sx={{ 
              color: "primary.main",
              position: "relative",
              "&::after": {
                content: '""',
                position: "absolute",
                bottom: -4,
                left: 0,
                right: 0,
                height: "3px",
                background: "linear-gradient(90deg, transparent, currentColor, transparent)",
                opacity: 0.3,
              }
            }}
          >
            Albums
          </Box>
        </Typography>

        {/* Search Input */}
        <Box
          sx={{
            width: "100%",
            maxWidth: "600px",
            mb: { xs: 3, sm: 4 },
            px: { xs: 2, sm: 0 },
          }}
        >
          <TextField
            value={searchTerm}
            onChange={handleSearchChange}
            placeholder="Search albums, artists, genres..."
            fullWidth
            variant="outlined"
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon sx={{ color: "text.secondary" }} />
                </InputAdornment>
              ),
              sx: {
                borderRadius: 3,
                backgroundColor: "background.paper",
                transition: "all 0.3s ease-in-out",
                "&:hover": {
                  backgroundColor: "action.hover",
                },
                "&.Mui-focused": {
                  backgroundColor: "background.paper",
                  "& .MuiOutlinedInput-notchedOutline": {
                    borderWidth: "2px",
                  },
                },
              },
            }}
            sx={{
              "& .MuiOutlinedInput-root": {
                "& fieldset": {
                  borderColor: "divider",
                },
              },
            }}
            aria-label="Search albums"
          />
        </Box>

        {/* Category Tabs */}
        <Box
          sx={{
            width: "100%",
            display: "flex",
            justifyContent: "center",
            mb: { xs: 2, sm: 3 },
            px: { xs: 2, sm: 0 },
          }}
        >
          <Tabs
            value={selectedCategory}
            onChange={handleCategoryChange}
            textColor="primary"
            indicatorColor="primary"
            variant="scrollable"
            scrollButtons="auto"
            sx={{
              "& .MuiTabs-indicator": {
                height: 3,
                borderRadius: "3px 3px 0 0",
              },
              "& .MuiTab-root": {
                fontSize: { xs: "0.875rem", sm: "1rem" },
                fontWeight: 600,
                letterSpacing: "0.05em",
                minWidth: { xs: 80, sm: 100 },
                px: { xs: 2, sm: 3 },
                py: 1.5,
                transition: "all 0.3s ease-in-out",
                "&:hover": {
                  backgroundColor: "action.hover",
                  transform: "translateY(-2px)",
                },
                "&.Mui-selected": {
                  color: "primary.main",
                },
              },
              "& .MuiTabs-scrollButtons": {
                "&.Mui-disabled": {
                  opacity: 0.3,
                },
              },
            }}
          >
            {CATEGORIES.map((category) => (
              <Tab 
                key={category.value}
                label={category.label}
                value={category.value}
                aria-label={`Show ${category.label.toLowerCase()} albums`}
              />
            ))}
          </Tabs>
        </Box>

        {/* Items Grid */}
        <Box sx={{ width: "100%" }}>
          {renderContent()}
        </Box>

        {/* Results Count */}
        {!isLoading && filteredItems.length > 0 && (
          <Fade in timeout={500}>
            <Typography 
              variant="body2" 
              color="text.secondary" 
              textAlign="center"
              sx={{ 
                mt: 3,
                px: 2,
                fontWeight: 500,
              }}
            >
              Showing {filteredItems.length} {filteredItems.length === 1 ? 'album' : 'albums'}
              {searchTerm && ` matching "${searchTerm}"`}
              {selectedCategory !== 'all' && ` in ${selectedCategory.toUpperCase()}`}
            </Typography>
          </Fade>
        )}
      </Container>
    </Box>
  );
};

export default ShoppingList;
