import React from 'react';
import { TextField, Button, Box } from '@mui/material';
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';

const SignupSchema = Yup.object().shape({
  firstName: Yup.string()
    .min(2, 'Too Short!')
    .max(50, 'Too Long!')
    .required('Required'),
  lastName: Yup.string()
    .min(2, 'Too Short!')
    .max(50, 'Too Long!')
    .required('Required'),
  email: Yup.string().email('Invalid email').required('Required'),
});

const SignupForm = () => {
  return (
    <Box style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      <Formik
        initialValues={{
          firstName: '',
          lastName: '',
          email: '',
        }}
        validationSchema={SignupSchema}
        onSubmit={(values) => {
          localStorage.setItem('signupData', JSON.stringify(values));
          alert(JSON.stringify(values, null, 2));
        }}
      >
        {({ errors, touched }) => (
          <Form style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <Field as={TextField} name="firstName" label="First Name" error={touched.firstName && Boolean(errors.firstName)} helperText={touched.firstName && errors.firstName} />
            <Field as={TextField} name="lastName" label="Last Name" error={touched.lastName && Boolean(errors.lastName)} helperText={touched.lastName && errors.lastName} />
            <Field as={TextField} name="email" label="Email" error={touched.email && Boolean(errors.email)} helperText={touched.email && errors.email} />
            <Button type="submit" variant="contained" color="primary">
              Submit
            </Button>
          </Form>
        )}
      </Formik>
    </Box>
  );
};

export default SignupForm;
