import React, { useState, useEffect, useCallback, useMemo } from "react";
import { Box, Typography, useMediaQuery } from "@mui/material";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import FavoriteIcon from "@mui/icons-material/Favorite";
import { addToCart } from "../../state";
import Item from "../../components/Item";

// Constants
const API_URL = "https://sprint9-ecommerce-production.up.railway.app/api/items";

const ItemDetails = () => {
  const dispatch = useDispatch();
  const { itemId } = useParams();
  const cart = useSelector((state) => state.cart.cart);

  const [activeTab, setActiveTab] = useState("description");
  const [item, setItem] = useState(null);
  const [items, setItems] = useState([]);
  const [isInWishlist, setIsInWishlist] = useState(false);

  const isMobile = useMediaQuery("(max-width:768px)");
  const isTablet = useMediaQuery("(max-width:1024px)");

  // Memoized values
  const isItemInCart = useMemo(() => 
    cart.some((cartItem) => cartItem.id === item?.id), 
    [cart, item]
  );

  // Responsive values
  const responsiveValues = useMemo(() => ({
    containerWidth: isMobile ? "90%" : isTablet ? "85%" : "80%",
    containerMargin: isMobile ? "40px auto" : isTablet ? "60px auto" : "80px auto",
    layoutDirection: isMobile ? "column" : "row",
    imageFlex: isMobile ? "1 1 100%" : "1 1 45%",
    infoFlex: isMobile ? "1 1 100%" : "1 1 50%",
    gap: isMobile ? "20px" : "40px",
  }), [isMobile, isTablet]);

  // Handlers
  const handleTabChange = useCallback((event, newValue) => {
    setActiveTab(newValue);
  }, []);

  const handleWishlistClick = useCallback(() => {
    setIsInWishlist(!isInWishlist);
  }, [isInWishlist]);

  const handleAddToCart = useCallback(() => {
    if (!isItemInCart && item) {
      dispatch(addToCart({ item: { ...item, count: 1 } }));
    }
  }, [dispatch, isItemInCart, item]);

  // Data fetching
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch current item
        const itemResponse = await fetch(`${API_URL}/${itemId}?populate=image`);
        if (!itemResponse.ok) throw new Error('Item fetch failed');
        const itemJson = await itemResponse.json();
        setItem(itemJson.data);

        // Fetch all items
        const itemsResponse = await fetch(`${API_URL}?populate=image`);
        if (!itemsResponse.ok) throw new Error('Items fetch failed');
        const itemsJson = await itemsResponse.json();
        setItems(itemsJson.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [itemId]);

  // Helper functions
  const getImageUrl = useCallback((item) => {
    if (!item?.attributes?.image?.data?.attributes?.formats?.medium?.url) {
      return "/default-image.jpg";
    }
    return `https://sprint9-ecommerce-production.up.railway.app${item.attributes.image.data.attributes.formats.medium.url}`;
  }, []);

  const renderNavigationLink = useCallback((navItem, label) => {
    if (!navItem) return null;

    return (
      <Link to={`/item/${navItem.id}`} style={{ textDecoration: "none", color: "inherit" }}>
        <Typography variant="body2" sx={{ fontSize: '0.9rem', color: '#666' }}>
          {label}
        </Typography>
      </Link>
    );
  }, []);

  // Computed values
  const { relatedItems, prevItem, nextItem } = useMemo(() => {
    if (!item || !items.length) {
      return { relatedItems: [], prevItem: null, nextItem: null };
    }

    const currentIndex = items.findIndex((i) => i.id === parseInt(itemId));
    const category = item.attributes?.category;

    return {
      relatedItems: items.filter(
        (relatedItem) =>
          relatedItem?.attributes?.category === category &&
          relatedItem.id !== item.id
      ).slice(0, 4),
      prevItem: currentIndex > 0 ? items[currentIndex - 1] : null,
      nextItem: currentIndex < items.length - 1 ? items[currentIndex + 1] : null,
    };
  }, [item, items, itemId]);

  // Loading state
  if (!item?.attributes) {
    return (
      <Box 
        width={responsiveValues.containerWidth} 
        margin={responsiveValues.containerMargin}
        textAlign="center"
      >
        <Typography variant="h4" sx={{ color: '#000', fontWeight: 400 }}>
          Loading...
        </Typography>
      </Box>
    );
  }

  const { name, price, category, tracklist, longDescription, shortDescription, mediaCondition, sleeveCondition } = item.attributes;

  // Styles
  const styles = {
    container: {
      width: responsiveValues.containerWidth,
      margin: responsiveValues.containerMargin,
    },
    mainContent: {
      display: 'flex',
      flexDirection: responsiveValues.layoutDirection,
      flexWrap: 'wrap',
      gap: responsiveValues.gap,
      marginBottom: '40px',
    },
    imageSection: {
      flex: responsiveValues.imageFlex,
    },
    image: {
      width: '100%',
      height: 'auto',
      aspectRatio: '1 / 1',
      objectFit: 'cover',
      backgroundColor: '#f8f8f8',
    },
    infoSection: {
      flex: responsiveValues.infoFlex,
      display: 'flex',
      flexDirection: 'column',
    },
    navigation: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '40px',
    },
    navLinks: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
    },
    divider: {
      color: '#666',
      fontSize: '0.9rem',
    },
    productInfo: {
      marginBottom: '40px',
    },
    productName: {
      fontSize: { xs: '1.5rem', sm: '2rem' },
      fontWeight: 400,
      color: '#000',
      marginBottom: '16px',
      fontFamily: 'system-ui, sans-serif',
    },
    productPrice: {
      fontSize: '1.2rem',
      fontWeight: 400,
      color: '#000',
      marginBottom: '20px',
      fontFamily: 'system-ui, sans-serif',
    },
    tracklist: {
      fontSize: '0.9rem',
      color: '#666',
      marginBottom: '20px',
      lineHeight: 1.6,
    },
    description: {
      fontSize: '0.9rem',
      color: '#666',
      lineHeight: 1.6,
      marginTop: '20px',
    },
    addToCartButton: {
      backgroundColor: isItemInCart ? '#999' : '#000',
      color: '#fff',
      borderRadius: '0px',
      padding: '12px 32px',
      fontSize: '0.8rem',
      fontWeight: 400,
      textTransform: 'uppercase',
      letterSpacing: '0.5px',
      minWidth: '160px',
      marginBottom: '24px',
      transition: 'opacity 0.3s ease',
      '&:hover': {
        backgroundColor: isItemInCart ? '#999' : '#000',
        opacity: 0.8,
      },
    },
    wishlistSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      marginBottom: '16px',
      cursor: 'pointer',
    },
    wishlistText: {
      fontSize: '0.8rem',
      color: '#666',
      textTransform: 'uppercase',
      letterSpacing: '0.5px',
    },
    category: {
      fontSize: '0.8rem',
      color: '#666',
      textTransform: 'uppercase',
      letterSpacing: '0.5px',
    },
    tabs: {
      margin: '40px 0',
      borderBottom: '1px solid #f0f0f0',
      '& .MuiTab-root': {
        fontSize: '0.8rem',
        fontWeight: 400,
        textTransform: 'uppercase',
        letterSpacing: '0.5px',
        color: '#666',
        minWidth: 'auto',
        padding: '12px 24px',
      },
    },
    tabContent: {
      fontSize: '0.9rem',
      color: '#666',
      lineHeight: 1.6,
    },
    relatedSection: {
      marginTop: '60px',
      width: '100%',
    },
    relatedTitle: {
      fontSize: '1.2rem',
      fontWeight: 400,
      color: '#000',
      marginBottom: '24px',
      fontFamily: 'system-ui, sans-serif',
    },
    relatedGrid: {
      display: 'grid',
      gridTemplateColumns: {
        xs: '1fr',
        sm: 'repeat(2, 1fr)',
        md: 'repeat(4, 1fr)',
      },
      gap: '24px',
      width: '100%',
    },
  };

  return (
    <Box sx={styles.container}>
      {/* Main Content */}
      <Box sx={styles.mainContent}>
        {/* Image Section */}
        <Box sx={styles.imageSection}>
          <img
            alt={name}
            src={getImageUrl(item)}
            sx={styles.image}
          />
        </Box>

        {/* Info Section */}
        <Box sx={styles.infoSection}>
          {/* Navigation */}
          <Box sx={styles.navigation}>
            <Link to="/" style={{ textDecoration: "none" }}>
              <Typography variant="body2" sx={{ fontSize: '0.9rem', color: '#666' }}>
                Home
              </Typography>
            </Link>
            <Box sx={styles.navLinks}>
              {renderNavigationLink(prevItem, "Previous")}
              {prevItem && nextItem && <Typography sx={styles.divider}>|</Typography>}
              {renderNavigationLink(nextItem, "Next")}
            </Box>
          </Box>

          {/* Product Info */}
          <Box sx={styles.productInfo}>
            <Typography sx={styles.productName}>
              {name}
            </Typography>
            <Typography sx={styles.productPrice}>
              â‚¬{price?.toFixed(2) || "N/A"}
            </Typography>

            {tracklist && (
              <Typography sx={styles.tracklist}>
                <strong>Tracklist:</strong> {tracklist}
              </Typography>
            )}

            {longDescription && (
              <Typography sx={styles.description}>
                {longDescription.split("\n").map((line, i) => (
                  <span key={i}>
                    {line}
                    <br />
                  </span>
                ))}
              </Typography>
            )}
          </Box>

          {/* Actions */}
          <Box
            component="button"
            onClick={handleAddToCart}
            disabled={isItemInCart}
            sx={styles.addToCartButton}
          >
            {isItemInCart ? "In Cart" : "Add to Cart"}
          </Box>

          {/* Wishlist & Category */}
          <Box sx={styles.wishlistSection} onClick={handleWishlistClick}>
            {isInWishlist ? (
              <FavoriteIcon sx={{ fontSize: '20px', color: '#000' }} />
            ) : (
              <FavoriteBorderOutlinedIcon sx={{ fontSize: '20px', color: '#666' }} />
            )}
            <Typography sx={styles.wishlistText}>
              {isInWishlist ? "Remove from Wishlist" : "Add to Wishlist"}
            </Typography>
          </Box>

          <Typography sx={styles.category}>
            Category: {category || "Unknown"}
          </Typography>
        </Box>
      </Box>

      {/* Tabs Section */}
      <Tabs value={activeTab} onChange={handleTabChange} sx={styles.tabs}>
        <Tab label="Description" value="description" />
        <Tab label="Condition" value="condition" />
      </Tabs>

      <Box sx={styles.tabContent}>
        {activeTab === "description" && shortDescription && (
          <div>
            {shortDescription.split("\n").map((line, i) => (
              <Typography key={i} component="p" sx={{ marginBottom: '8px' }}>
                {line}
              </Typography>
            ))}
          </div>
        )}
        {activeTab === "condition" && (
          <div>
            {mediaCondition && (
              <Typography component="p" sx={{ marginBottom: '8px' }}>
                <strong>Media Condition:</strong> {mediaCondition}
              </Typography>
            )}
            {sleeveCondition && (
              <Typography component="p">
                <strong>Sleeve Condition:</strong> {sleeveCondition}
              </Typography>
            )}
          </div>
        )}
      </Box>

      {/* Related Products */}
      {relatedItems.length > 0 && (
        <Box sx={styles.relatedSection}>
          <Typography sx={styles.relatedTitle}>
            Related Albums
          </Typography>
          <Box sx={styles.relatedGrid}>
            {relatedItems.map((relatedItem) => (
              <Item 
                key={relatedItem.id} 
                item={relatedItem} 
              />
            ))}
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default ItemDetails;
