import React, { useState, useCallback, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography, IconButton } from "@mui/material";
import { AddShoppingCart, Check } from "@mui/icons-material";
import { shades } from "../theme";
import { addToCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item, style }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [isHovered, setIsHovered] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  // Memoized values and handlers
  const handleAddToCart = useCallback((e) => {
    e.stopPropagation();
    if (item?.attributes) {
      const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
      if (!isItemInCart) {
        dispatch(addToCart({ item: { ...item, count: 1 } }));
      }
    }
  }, [cart, dispatch, item]);

  const handleNavigate = useCallback(() => {
    if (item?.id) {
      navigate(`/item/${item.id}`);
    }
  }, [navigate, item]);

  // Extract and memoize item data
  const { itemData, isItemInCart, imageUrl } = useMemo(() => {
    if (!item?.attributes) {
      return { itemData: null, isItemInCart: false, imageUrl: "" };
    }

    const { category = "Unknown", price = 0, name = "Unnamed Item", image } = item.attributes;

    const imageUrl = image?.data?.attributes?.formats?.medium?.url 
      ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
      : "/default-image.jpg";

    const isInCart = cart.some((cartItem) => cartItem.id === item.id);

    return {
      itemData: { category, price, name },
      isItemInCart: isInCart,
      imageUrl
    };
  }, [item, cart]);

  // Format category name
  const formatCategory = useCallback((category) => {
    return category
      .replace(/([A-Z])/g, " $1")
      .replace(/^./, (str) => str.toUpperCase())
      .trim();
  }, []);

  // Loading/Error placeholder
  if (!item?.attributes) {
    return (
      <Box 
        sx={{ 
          maxWidth: { xs: '100%', sm: '400px' }, 
          margin: '0 auto',
          ...style 
        }}
      >
        <Box
          sx={{
            backgroundColor: shades.neutral[100],
            aspectRatio: '1 / 1',
            width: '100%',
            borderRadius: '2px',
            animation: 'pulse 2s ease-in-out infinite',
            '@keyframes pulse': {
              '0%, 100%': { opacity: 0.6 },
              '50%': { opacity: 0.4 },
            },
          }}
        />
        <Box mt={2}>
          <Box sx={{ 
            backgroundColor: shades.neutral[100], 
            height: '12px', 
            mb: 1, 
            borderRadius: '1px', 
            width: '30%' 
          }} />
          <Box sx={{ 
            backgroundColor: shades.neutral[100], 
            height: '16px', 
            mb: 0.5, 
            borderRadius: '1px', 
            width: '70%' 
          }} />
          <Box sx={{ 
            backgroundColor: shades.neutral[100], 
            height: '14px', 
            borderRadius: '1px', 
            width: '25%', 
            mt: 1 
          }} />
        </Box>
      </Box>
    );
  }

  const { category, price, name } = itemData;

  // Elegant color palette
  const colors = {
    primary: shades.primary[500],
    neutral: {
      100: shades.neutral[100],
      300: shades.neutral[300],
      500: shades.neutral[500],
      700: shades.neutral[700],
      900: shades.neutral[900],
    },
    accent: '#2D2C2F' // Dark elegant color
  };

  const styles = {
    container: {
      cursor: 'pointer',
      position: 'relative',
      maxWidth: { xs: '100%', sm: '400px' },
      margin: '0 auto',
      transition: 'all 0.4s cubic-bezier(0.16, 1, 0.3, 1)',
      border: `1px solid ${colors.neutral[300]}`,
      borderRadius: '1px',
      backgroundColor: 'white',
      overflow: 'hidden',
      '&:hover': {
        borderColor: colors.neutral[500],
        transform: 'translateY(-2px)',
      },
      ...style
    },
    imageContainer: {
      position: 'relative',
      overflow: 'hidden',
      backgroundColor: colors.neutral[100],
      aspectRatio: '1 / 1',
      transition: 'all 0.6s cubic-bezier(0.16, 1, 0.3, 1)',
    },
    image: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
      transition: 'all 0.8s cubic-bezier(0.16, 1, 0.3, 1)',
      transform: isHovered ? 'scale(1.02)' : 'scale(1)',
      opacity: imageLoaded ? 1 : 0,
      filter: isHovered ? 'grayscale(0.3)' : 'grayscale(0)',
    },
    overlay: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: `linear-gradient(45deg, ${colors.primary}02 0%, transparent 50%)`,
      opacity: isHovered ? 1 : 0,
      transition: 'opacity 0.4s ease',
      pointerEvents: 'none',
    },
    cartButton: {
      position: 'absolute',
      top: '16px',
      right: '16px',
      backgroundColor: 'white',
      color: isItemInCart ? colors.primary : colors.accent,
      width: '44px',
      height: '44px',
      borderRadius: '0px',
      transition: 'all 0.4s cubic-bezier(0.16, 1, 0.3, 1)',
      opacity: isHovered ? 1 : 0,
      transform: isHovered ? 'scale(1) rotate(0deg)' : 'scale(0.8) rotate(-45deg)',
      boxShadow: '0 2px 12px rgba(0,0,0,0.1)',
      '&:hover': {
        backgroundColor: colors.primary,
        color: 'white',
        transform: 'scale(1.1)',
      },
      '& .MuiSvgIcon-root': {
        fontSize: '1.2rem',
        transition: 'transform 0.3s ease',
      }
    },
    infoContainer: {
      padding: { xs: '16px', sm: '20px' },
      borderTop: `1px solid ${colors.neutral[300]}`,
      transition: 'border-color 0.4s ease',
    },
    category: {
      color: colors.neutral[500],
      textTransform: 'uppercase',
      letterSpacing: '2px',
      fontSize: { xs: '0.6rem', sm: '0.65rem' },
      fontWeight: 400,
      display: 'block',
      mb: 1,
      fontFamily: '"Jost", sans-serif',
    },
    name: {
      fontWeight: 400,
      fontSize: { xs: '0.9rem', sm: '1rem' },
      color: colors.accent,
      mb: 1,
      lineHeight: 1.4,
      letterSpacing: '0.5px',
      fontFamily: '"Jost", sans-serif',
      minHeight: { xs: '2.8rem', sm: '3rem' },
    },
    priceContainer: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      mt: 1.5,
    },
    price: {
      fontWeight: 300,
      fontSize: { xs: '1rem', sm: '1.1rem' },
      color: colors.accent,
      letterSpacing: '1px',
      fontFamily: '"Jost", sans-serif',
    },
    divider: {
      flex: 1,
      height: '1px',
      backgroundColor: colors.neutral[300],
      margin: '0 12px',
      transition: 'background-color 0.4s ease',
    }
  };

  return (
    <Box
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={handleNavigate}
      sx={styles.container}
    >
      {/* Image Container */}
      <Box sx={styles.imageContainer}>
        <Box
          component="img"
          alt={name}
          src={imageUrl}
          onLoad={() => setImageLoaded(true)}
          sx={styles.image}
        />

        {/* Subtle Overlay */}
        <Box sx={styles.overlay} />

        {/* Minimal Cart Button */}
        <IconButton
          onClick={handleAddToCart}
          disabled={isItemInCart}
          sx={styles.cartButton}
          aria-label={isItemInCart ? "Item in cart" : "Add to cart"}
        >
          {isItemInCart ? <Check /> : <AddShoppingCart />}
        </IconButton>
      </Box>

      {/* Product Info */}
      <Box sx={styles.infoContainer}>
        <Typography sx={styles.category}>
          {formatCategory(category)}
        </Typography>

        <Typography sx={styles.name}>
          {name}
        </Typography>

        <Box sx={styles.priceContainer}>
          <Box sx={styles.divider} />
          <Typography sx={styles.price}>
            €{price.toFixed(2)}
          </Typography>
        </Box>
      </Box>
    </Box>
  );
};

export default Item;
