import React, { useEffect, useState, useMemo, useCallback } from "react";
import { 
  Tabs, 
  Tab, 
  Box, 
  Typography, 
  Input,
  useMediaQuery,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";

// Constants for better maintainability
const API_URL = "https://sprint9-ecommerce-production.up.railway.app/api/items?populate=image&pagination[limit]=50";
const CATEGORIES = [
  { label: "ALL", value: "all" },
  { label: "JAZZ", value: "jazz" },
  { label: "POP", value: "pop" },
  { label: "ROCK", value: "rock" },
  { label: "SOUL", value: "soul" },
];

const BREAKPOINTS = {
  mobile: 600,
  tablet: 960,
};

const ShoppingList = () => {
  const dispatch = useDispatch();
  const isMobile = useMediaQuery(`(max-width:${BREAKPOINTS.mobile}px)`);
  const isTablet = useMediaQuery(`(max-width:${BREAKPOINTS.tablet}px)`);

  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const items = useSelector((state) => state.cart.items);

  // Responsive values
  const responsiveValues = useMemo(() => ({
    containerWidth: isMobile ? "90%" : isTablet ? "85%" : "80%",
    containerMargin: isMobile ? "60px auto" : "80px auto",
    titleVariant: isMobile ? "h4" : "h3",
    searchMargin: isMobile ? "5px" : "10px",
    tabsMargin: isMobile ? "15px" : "25px",
    gridGap: isMobile ? "15px" : "20px",
    gridColumns: {
      xs: "1fr",
      sm: "repeat(auto-fill, minmax(280px, 1fr))",
      md: "repeat(auto-fill, minmax(350px, 1fr))",
      lg: "repeat(auto-fill, minmax(400px, 1fr))",
    },
  }), [isMobile, isTablet]);

  // Event handlers
  const handleCategoryChange = useCallback((event, newValue) => {
    setSelectedCategory(newValue);
  }, []);

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value);
  }, []);

  // Filter items based on category and search term
  const filteredItems = useMemo(() => {
    return items.filter(item => {
      const matchesCategory = selectedCategory === "all" || 
        item.attributes?.category?.toLowerCase() === selectedCategory;

      const matchesSearch = searchTerm.trim() === "" ||
        item.attributes?.name?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  }, [items, selectedCategory, searchTerm]);

  // Listen for category changes from MainCarousel
  useEffect(() => {
    const handleExternalCategoryChange = (event) => {
      setSelectedCategory(event.detail.category);
    };

    window.addEventListener('categoryChange', handleExternalCategoryChange);

    // Check for saved category in sessionStorage
    const savedCategory = sessionStorage.getItem('selectedCategory');
    if (savedCategory && CATEGORIES.some(cat => cat.value === savedCategory)) {
      setSelectedCategory(savedCategory);
      sessionStorage.removeItem('selectedCategory');
    }

    return () => {
      window.removeEventListener('categoryChange', handleExternalCategoryChange);
    };
  }, []);

  // Fetch items from API
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await fetch(API_URL, { 
          method: "GET",
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const itemsData = await response.json();

        if (itemsData.data && Array.isArray(itemsData.data)) {
          dispatch(setItems(itemsData.data));
        } else {
          console.error("Unexpected API response structure:", itemsData);
          dispatch(setItems([]));
        }
      } catch (error) {
        console.error("Error fetching items:", error);
        dispatch(setItems([]));
      }
    };

    fetchItems();
  }, [dispatch]);

  // Render loading state
  const renderContent = () => {
    if (items.length === 0) {
      return (
        <Typography 
          variant="h6" 
          textAlign="center" 
          color="text.secondary"
          sx={{ mt: 4 }}
        >
          Loading albums...
        </Typography>
      );
    }

    if (filteredItems.length === 0) {
      return (
        <Typography 
          variant="h6" 
          textAlign="center" 
          color="text.secondary"
          sx={{ mt: 4 }}
        >
          {searchTerm 
            ? `No albums found for "${searchTerm}" in ${selectedCategory === 'all' ? 'any category' : selectedCategory}`
            : `No albums found in ${selectedCategory} category`
          }
        </Typography>
      );
    }

    return (
      <Box
        margin="0 auto"
        display="grid"
        gridTemplateColumns={responsiveValues.gridColumns}
        justifyContent="space-around"
        rowGap={responsiveValues.gridGap}
        columnGap="1.33%"
        p={1}
      >
        {filteredItems.map((item) => (
          <Item
            item={item}
            key={`item-${item.id}-${item.attributes?.name}`}
            style={{ maxWidth: "100%" }}
          />
        ))}
      </Box>
    );
  };

  return (
    <Box 
      component="section"
      aria-label="Music albums collection"
      width={responsiveValues.containerWidth}
      margin={responsiveValues.containerMargin}
      id="shopping-list"
    >
      {/* Header */}
      <Typography 
        variant={responsiveValues.titleVariant} 
        component="h2"
        textAlign="center"
        sx={{ 
          mb: 2,
          fontWeight: 600,
        }}
      >
        Our Featured <Box component="span" sx={{ color: "primary.main", fontWeight: 700 }}>Albums</Box>
      </Typography>

      {/* Search Input */}
      <Input
        value={searchTerm}
        onChange={handleSearchChange}
        placeholder="Search albums..."
        fullWidth
        sx={{
          m: responsiveValues.searchMargin,
          transition: "all 0.3s ease-in-out",
          transform: searchTerm ? "translateX(0)" : "translateX(-50px)",
          opacity: searchTerm ? 1 : 0.7,
          '&:focus-within': {
            transform: "translateX(0)",
            opacity: 1,
          },
        }}
        aria-label="Search albums"
      />

      {/* Category Tabs */}
      <Tabs
        value={selectedCategory}
        onChange={handleCategoryChange}
        textColor="primary"
        indicatorColor="primary"
        centered={!isMobile}
        variant={isMobile ? "scrollable" : "standard"}
        scrollButtons={isMobile ? "auto" : false}
        TabIndicatorProps={{ 
          sx: { display: isMobile ? "none" : "block" } 
        }}
        sx={{
          m: responsiveValues.tabsMargin,
          '& .MuiTabs-flexContainer': {
            flexWrap: isMobile ? "nowrap" : "wrap",
            justifyContent: isMobile ? "flex-start" : "center",
          },
          '& .MuiTab-root': {
            fontSize: isMobile ? "0.8rem" : "0.9rem",
            minWidth: isMobile ? "auto" : undefined,
            px: isMobile ? 2 : 3,
          },
        }}
      >
        {CATEGORIES.map((category) => (
          <Tab 
            key={category.value}
            label={category.label}
            value={category.value}
            aria-label={`Show ${category.label.toLowerCase()} albums`}
          />
        ))}
      </Tabs>

      {/* Items Grid */}
      {renderContent()}

      {/* Results Count */}
      {filteredItems.length > 0 && (
        <Typography 
          variant="body2" 
          color="text.secondary" 
          textAlign="center"
          sx={{ mt: 2 }}
        >
          Showing {filteredItems.length} of {items.length} albums
          {searchTerm && ` for "${searchTerm}"`}
          {selectedCategory !== 'all' && ` in ${selectedCategory}`}
        </Typography>
      )}
    </Box>
  );
};

export default ShoppingList;
