import React from "react";
import { Box, Typography, IconButton } from "@mui/material";
import { Twitter, Instagram } from "@mui/icons-material";
import { useTheme } from "@emotion/react";
import logo from "../../assets/logo/logo-imagen.png";
import { Link } from "react-router-dom";

function Footer() {
  const theme = useTheme();

  const linkColor = "#FFC709"; // amarillo

  return (
    <Box
      height="200px"
      backgroundColor="#2D2C2F"
      color={theme.palette.primary.main}
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <Box
        width="80%"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
      >
        <Box display="flex" alignItems="center">
          <img
            src={logo}
            alt="Logo"
            style={{ height: "150px", width: "auto" }}
          />
        </Box>

        <Box
          display="flex"
          flexDirection="column"
          alignItems="flex-end"
          justifyContent="center"
          rowGap="10px"
        >
          {["About Us", "Contact Us", "Grading", "Shipping Terms"].map(
            (item, index) => {
              const isLink = item === "About Us";
              const content = (
                <Typography
                  variant="h4"
                  fontWeight="bold"
                  fontSize="1.2em"
                  sx={{
                    position: "relative",
                    color: linkColor,
                    cursor: isLink ? "pointer" : "default",
                    textDecoration: isLink ? "underline" : "none",
                    fontFamily: "'Bebas Neue', cursive",
                    padding: "0 8px",
                    display: "inline-block",
                  }}
                >
                  <span
                    style={{
                      position: "relative",
                      display: "inline-block",
                      backgroundImage: `linear-gradient(${linkColor}, ${linkColor})`,
                      backgroundSize: "100% 10px",
                      backgroundRepeat: "no-repeat",
                      backgroundPosition: "100% 0%",
                      transition:
                        "background-size 0.7s ease, background-position 0.5s ease-in-out",
                    }}
                    onMouseEnter={(e) => {
                      e.currentTarget.style.backgroundSize = "100% 100%";
                      e.currentTarget.style.backgroundPosition = "0% 100%";
                    }}
                    onMouseLeave={(e) => {
                      e.currentTarget.style.backgroundSize = "100% 10px";
                      e.currentTarget.style.backgroundPosition = "100% 0%";
                    }}
                  >
                    {item}
                  </span>
                </Typography>
              );

              if (isLink) {
                return (
                  <Link
                    key={index}
                    to="/about-us"
                    style={{ textDecoration: "none" }}
                    aria-label={item}
                  >
                    {content}
                  </Link>
                );
              }
              return <Box key={index}>{content}</Box>;
            }
          )}

          <Box display="flex" gap={1}>
            {[
              {
                icon: <Twitter />,
                link: "https://twitter.com/allmyrecords",
              },
              {
                icon: <Instagram />,
                link: "https://www.instagram.com/allmyrecords/",
              },
            ].map((item, index) => (
              <IconButton
                key={index}
                href={item.link}
                target="_blank"
                rel="noopener noreferrer"
                sx={{ color: linkColor }}
                aria-label={item.link}
              >
                {item.icon}
              </IconButton>
            ))}
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default Footer;
