import { useState } from "react";
import { getIn } from "formik";
import { Formik } from "formik";
import { Box, Button } from "@mui/material";
import TextField from "@mui/material/TextField";
import useMediaQuery from "@mui/material/useMediaQuery";

const SignupForm = ({
  values,
  touched,
  errors,
  handleBlur,
  handleChange,
  handleSubmit,
}) => {
  const isNonMobile = useMediaQuery("(min-width:600px)");

  const formattedName = (field) => `${field}`;

  const formattedError = (field) =>
    Boolean(getIn(touched, formattedName(field)) && getIn(errors, formattedName(field)));

  const formattedHelper = (field) =>
    getIn(touched, formattedName(field)) && getIn(errors, formattedName(field));

  const [submitted, setSubmitted] = useState(false);

  const handleSubmitForm = (e) => {
    e.preventDefault();
    handleSubmit();
    setSubmitted(true);
  };

  const saveDataToLocalStorage = (data) => {
    const existingData = JSON.parse(localStorage.getItem("user")) || {};
    const newData = { ...existingData, ...data };
    localStorage.setItem("user", JSON.stringify(newData));
  };

  return (
    <Box
      component="form"
      onSubmit={handleSubmitForm}
      display="grid"
      gap="15px"
      gridTemplateColumns="repeat(4, minmax(0, 1fr))"
      sx={{
        "& > div": { gridColumn: isNonMobile ? undefined : "span 4" },
      }}
    >
      <TextField
  fullWidth
  type="text"
  label="First Name"
  onBlur={handleBlur}
  onChange={handleChange}
  value={values?.firstName || ''}
  name={formattedName("firstName")}
  error={formattedError("firstName")}
  helperText={formattedHelper("firstName")}
  sx={{ gridColumn: "span 2" }}
/>
      <TextField
        fullWidth
        type="text"
        label="Last Name"
        onBlur={handleBlur}
        onChange={handleChange}
        value={values?.lastName  || ''}
        name={formattedName("lastName")}
        error={formattedError("lastName")}
        helperText={formattedHelper("lastName")}
        sx={{ gridColumn: "span 2" }}
      />
      <TextField
        fullWidth
        type="email"
        label="Email"
        onBlur={handleBlur}
        onChange={handleChange}
        value={values?.email  || ''}
        name={formattedName("email")}
        error={formattedError("email")}
        helperText={formattedHelper("email")}
        sx={{ gridColumn: "span 4" }}
      />
      <Box gridColumn="span 4" display="flex" justifyContent="flex-end">
        <Button variant="contained" color="primary" type="submit">
          {submitted ? "Submitted!" : "Submit"}
        </Button>
      </Box>
    </Box>
  );
};

export default SignupForm;
