import React, { useEffect, useState } from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import Item from "../../components/Item";
import { Typography } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useDispatch, useSelector } from "react-redux";
import { setItems } from "../../state";

const ShoppingList = () => {
  const dispatch = useDispatch();
  const [value, setValue] = useState("all");
  const items = useSelector((state) => state.cart.items);
  const breakPoint = useMediaQuery("(min-width:600px)");
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredItems, setFilteredItems] = useState([]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  async function getItems() {
    const items = await fetch(
      "http://localhost:1337/api/items?populate=image",
      { method: "GET" }
    );
    const itemsJson = await items.json();
    dispatch(setItems(itemsJson.data));
  }

  useEffect(() => {
    getItems();
  }, []);

  const searchItems = (term) => {
    const filtered = items.filter(
      (item) =>
        item.attributes.name?.toLowerCase().includes(term.toLowerCase()) ||
        item.attributes.artist?.toLowerCase().includes(term.toLowerCase())
    );
    setFilteredItems(filtered);
  };

  useEffect(() => {
    searchItems(searchTerm);
  }, [searchTerm, items]);

  const JazzAlbums = items.filter(
    (item) => item.attributes.category === "jazz"
  );
  const SoulAlbums = items.filter(
    (item) => item.attributes.category === "soul"
  );
  const PopAlbums = items.filter((item) => item.attributes.category === "pop");
  const RockAlbums = items.filter(
    (item) => item.attributes.category === "rock"
  );

  return (
    <>
      <Typography variant="h3" sx={{ textAlign: "center", marginTop: 2 }}>
        Music Store
      </Typography>
      <Box sx={{ display: "flex", justifyContent: "center" }}>
        <Tabs value={value} onChange={handleChange}>
          <Tab label="All" value="all" />
          <Tab label="Jazz" value="jazz" />
          <Tab label="Soul" value="soul" />
          <Tab label="Pop" value="pop" />
          <Tab label="Rock" value="rock" />
        </Tabs>
      </Box>
      <Box sx={{ display: "flex", justifyContent: "center", marginTop: 2 }}>
        <TextField
          id="outlined-basic"
          label="Search"
          variant="outlined"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </Box>
      <Box sx={{ display: "flex", justifyContent: "center", flexWrap: "wrap" }}>
        {(value === "all" ? filteredItems : eval(`${value}Albums`)).map(
          (item) => (
            <Item key={item.id} item={item} breakPoint={breakPoint} />
          )
        )}
      </Box>
    </>
  );
};

export default ShoppingList;
