import React, { useState, useEffect } from "react";
import { Box, IconButton, useMediaQuery, Typography } from "@mui/material";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";
import { Link } from "react-router-dom";

const MainCarousel = () => {
  const isNonMobile = useMediaQuery("(min-width:600px)");
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setActiveIndex((prevIndex) =>
        prevIndex === genres.length - 1 ? 0 : prevIndex + 1
      );
    }, 2000);

    return () => {
      clearInterval(timer);
    };
  }, []);

  const importAll = (r) =>
    r.keys().reduce((acc, item) => {
      acc[item.replace("./", "")] = r(item);
      return acc;
    }, {});

  const heroTextureImports = importAll(
    require.context("../../assets", false, /\.(png|jpe?g|svg)$/)
  );

  const carouselImages = Object.values(heroTextureImports);

  const genres = ["Rock", "Jazz", "Pop", "Soul"];
  const subTitles = [
    "Powerful beats and raw energy",
    "Smooth rhythms and improvisation",
    "Catchy melodies and vibrant tunes",
    "Soulful vocals and emotional depth",
  ];

  return (
    <Carousel
      infiniteLoop={true}
      showThumbs={false}
      showIndicators={false}
      showStatus={false}
      selectedItem={activeIndex}
      transitionTime={3000}
      renderArrowPrev={(onClickHandler, hasPrev, label) => (
        <IconButton
          onClick={onClickHandler}
          sx={{
            position: "absolute",
            top: "50%",
            left: "0",
            color: "#FFC709",
            padding: "5px",
            zIndex: "10",
          }}
        >
          <NavigateBeforeIcon sx={{ fontSize: 40 }} />
        </IconButton>
      )}
      renderArrowNext={(onClickHandler, hasNext, label) => (
        <IconButton
          onClick={onClickHandler}
          sx={{
            position: "absolute",
            top: "50%",
            right: "0",
            color: "#FFC709",
            padding: "5px",
            zIndex: "10",
          }}
        >
          <NavigateNextIcon sx={{ fontSize: 40 }} />
        </IconButton>
      )}
    >
      {carouselImages.map((texture, index) => (
        <Box key={`carousel-image-${index}`}>
          <img
            src={texture}
            alt={`carousel-${index}`}
            style={{
              width: "100%",
              height: "700px",
              objectFit: "cover",
              backgroundAttachment: "fixed",
            }}
          />
          <Box
            position="absolute"
            color="#FFC709"
            top="50%"
            left="15%"
            transform="translate(-50%, -50%)"
            textAlign="left"
          >
            <Typography
              variant="h1"
              sx={{
                fontWeight: "bold",
                textTransform: "uppercase",
                fontSize: "4em",
                mb: 1,
              }}
            >
              <Link
                to={`/${genres[index].toLowerCase()}`}
                style={{ color: "white", textDecoration: "none" }}
              >
                {genres[index]}
              </Link>
            </Typography>
            <Typography
              variant="subtitle1"
              color="#FFC709"
              sx={{ textTransform: "uppercase", fontSize: "2em" }}
            >
              {subTitles[index]}
            </Typography>
          </Box>
        </Box>
      ))}
    </Carousel>
  );
};

export default MainCarousel;
