import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Badge, Box, IconButton, Input } from "@mui/material";
import {
  PersonOutline,
  ShoppingBagOutlined,
  SearchOutlined,
} from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { setIsCartOpen } from "../../state";
import logo from "../../assets/logo/logo.png";

function Navbar({ onSearchTermChange }) {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = (event) => {
    const value = event.target.value;
    setSearchTerm(value);
    onSearchTermChange(value); // Llama a la función de búsqueda en el componente principal
  };

  return (
    <Box
      display="flex"
      alignItems="center"
      width="100%"
      height="60px"
      backgroundColor="#2D2C2F"
      color="black"
      position="fixed"
      top="0"
      left="0"
      zIndex="1"
    >
      <Box
        width="80%"
        margin="auto"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
      >
        <Box
          onClick={() => navigate("/")}
          sx={{ "&:hover": { cursor: "pointer" } }}
          color="#FFC709"
          display="flex"
          alignItems="center"
        >
          <img
            src={logo}
            alt="Logo"
            style={{ marginRight: "8px", height: "35px", width: "auto" }}
          />
        </Box>
        <Box
          display="flex"
          justifyContent="space-between"
          columnGap="20px"
          zIndex="2"
        >
          <SearchOutlined />
          <Input
            value={searchTerm}
            onChange={handleSearch}
            placeholder="Search..."
            fullWidth
            sx={{ color: "#FFC709" }}
          />
          <IconButton
            sx={{ color: "#FFC709" }}
            onClick={() => navigate("/SignUP")}
          >
            <PersonOutline />
          </IconButton>
          <Badge
            badgeContent={cart.length}
            color="secondary"
            invisible={cart.length === 0}
            sx={{
              "& .MuiBadge-badge": {
                right: 5,
                top: 5,
                padding: "0 4px",
                height: "14px",
                minWidth: "13px",
              },
            }}
          >
            <IconButton
              onClick={() => dispatch(setIsCartOpen({}))}
              sx={{ color: "#FFC709" }}
            >
              <ShoppingBagOutlined />
            </IconButton>
          </Badge>
        </Box>
      </Box>
    </Box>
  );
}

export default Navbar;
