import { Box, Button, Typography } from "@mui/material";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { useParams, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import Item from "../../components/Item";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import { addToCart } from "../../state";
import { useDispatch } from "react-redux";

const ItemDetails = () => {
  const dispatch = useDispatch();
  const [count] = useState(1);
  const { itemId } = useParams();
  const [value, setValue] = useState("description");
  const [item, setItem] = useState(null);
  const [items, setItems] = useState([]);

  useEffect(() => {
    const getItem = async () => {
      const item = await fetch(
        `https://strapi-amr.onrender.com/api/items/${itemId}?populate=image`,
        {
          method: "GET",
        }
      );
      const itemJson = await item.json();
      setItem(itemJson.data);
    };

    const getItems = async () => {
      const items = await fetch(
        `https://strapi-amr.onrender.com/api/items?populate=image`,
        {
          method: "GET",
        }
      );
      const itemsJson = await items.json();
      setItems(itemsJson.data);
    };

    getItem();
    getItems();
  }, [itemId]);

  const currentIndex = items.findIndex((item) => item.id === String(itemId));
  console.log("Current Index:", currentIndex);

  useEffect(() => {
    if (currentIndex !== -1) {
      const prevItem =
        currentIndex > 0 ? items[currentIndex - 1] : items[items.length - 1];
      const nextItem =
        currentIndex < items.length - 1 ? items[currentIndex + 1] : items[0];
      console.log("Previous Item:", prevItem);
      console.log("Next Item:", nextItem);
    }
  }, [currentIndex, items]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const relatedItems = items.filter((relatedItem) => {
    return (
      item &&
      item.attributes &&
      item.attributes.category &&
      relatedItem.attributes.category === item.attributes.category &&
      relatedItem.id !== item.id
    );
  });

  return (
    <Box width="80%" m="80px auto">
      {/* ... (Resto del c√≥digo) ... */}
    </Box>
  );
};

export default ItemDetails;
