import React from "react";
import { Box, Typography, IconButton } from "@mui/material";
import { Twitter, Instagram } from "@mui/icons-material";
import { useTheme } from "@emotion/react";
import logo from "../../assets/logo/logo-imagen.png";
import { Link } from "react-router-dom";

function Footer() {
  const theme = useTheme();

  const linkColor = "#FFC709"; // color texto amarillo
  const hoverBackground = "#005E3A"; // color verde marca para fondo hover
  const backgroundColor = "#2D2C2F"; // fondo oscuro footer

  return (
    <Box
      height="200px"
      backgroundColor={backgroundColor}
      color={theme.palette.primary.main}
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <Box
        width="80%"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
      >
        <Box display="flex" alignItems="center">
          <img
            src={logo}
            alt="Logo"
            style={{ height: "150px", width: "auto" }}
          />
        </Box>

        <Box
          display="flex"
          flexDirection="column"
          alignItems="flex-end"
          justifyContent="center"
          rowGap="10px"
        >
          {["About Us", "Contact Us", "Grading", "Shipping Terms"].map(
            (item, index) => {
              const isLink = item === "About Us";

              const content = (
                <Typography
                  variant="h4"
                  fontWeight="bold"
                  fontSize="1.2em"
                  sx={{
                    color: linkColor,
                    textDecoration: isLink ? "underline" : "none",
                    position: "relative",
                    padding: "4px 0",
                    cursor: isLink ? "pointer" : "default",
                    fontFamily: "'Bebas Neue', cursive",
                    backgroundImage: `linear-gradient(${hoverBackground}, ${hoverBackground})`,
                    backgroundSize: "100% 10px",
                    backgroundRepeat: "no-repeat",
                    backgroundPosition: "100% 0%",
                    transition:
                      "background-size 0.7s, background-position 0.5s ease-in-out",
                    "&:hover": {
                      backgroundSize: "100% 100%",
                      backgroundPosition: "0% 100%",
                      transition:
                        "background-position 0.7s, background-size 0.5s ease-in-out",
                    },
                  }}
                >
                  {item}
                </Typography>
              );

              if (isLink) {
                return (
                  <Link
                    key={index}
                    to="/about-us"
                    style={{ textDecoration: "none" }}
                  >
                    {content}
                  </Link>
                );
              }
              return <Box key={index}>{content}</Box>;
            }
          )}

          <Box display="flex">
            {[
              {
                icon: <Twitter />,
                link: "https://twitter.com/allmyrecords",
              },
              {
                icon: <Instagram />,
                link: "https://www.instagram.com/allmyrecords/",
              },
            ].map((item, index) => (
              <IconButton
                key={index}
                href={item.link}
                target="_blank"
                rel="noopener noreferrer"
                sx={{ color: linkColor }}
              >
                {item.icon}
              </IconButton>
            ))}
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default Footer;
