import React, { useState } from "react";
import { Box, IconButton, useMediaQuery, Typography } from "@mui/material";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import NavigateBeforeIcon from "@mui/icons-material/NavigateBefore";
import NavigateNextIcon from "@mui/icons-material/NavigateNext";

const MainCarousel = () => {
  const esMobile = useMediaQuery("(max-width:600px)");
  const [activeIndex, setActiveIndex] = useState(0);

  const importAll = (r) =>
    r.keys().reduce((acc, item) => {
      acc[item.replace("./", "")] = r(item);
      return acc;
    }, {});

  const heroTextureImports = importAll(
    require.context("../../assets", false, /\.(png|jpe?g|svg)$/)
  );

  const carouselImages = Object.values(heroTextureImports);

  const genres = ["Rock", "Jazz", "Pop", "Soul"];
  const genreValues = ["rock", "jazz", "pop", "soul"];
  const subTitles = [
    "Electric guitars and rebellion spirit",
    "Smooth saxophones and midnight vibes",
    "Infectious hooks and feel-good energy",
    "Heartfelt lyrics and timeless grooves",
  ];

  // Función para manejar el click en el género
  const handleGenreClick = (genre, genreValue) => {
    sessionStorage.setItem('selectedCategory', genreValue);

    const shoppingListSection = document.getElementById('shopping-list');
    if (shoppingListSection) {
      shoppingListSection.scrollIntoView({ 
        behavior: 'smooth',
        block: 'start'
      });

      window.dispatchEvent(new CustomEvent('categoryChange', { 
        detail: { category: genreValue } 
      }));
    }
  };

  return (
    <Box sx={{ position: "relative", overflow: "hidden" }}>
      <style>
        {`
          .carousel .slider-wrapper {
            overflow: hidden;
          }

          .carousel .slider {
            display: flex;
            transition: transform 700ms cubic-bezier(0.4, 0, 0.2, 1) !important;
          }

          .carousel .slide {
            min-width: 100%;
            position: relative;
          }

          .carousel .slide img {
            transition: transform 700ms cubic-bezier(0.4, 0, 0.2, 1), 
                        filter 700ms cubic-bezier(0.4, 0, 0.2, 1);
          }

          .carousel .slide.selected img {
            transform: scale(1);
          }

          .carousel .slide:not(.selected) img {
            transform: scale(1.05);
          }
        `}
      </style>

      <Carousel
        infiniteLoop={true}
        showThumbs={false}
        showIndicators={false}
        showStatus={false}
        autoPlay={true}
        interval={5000}
        transitionTime={700}
        selectedItem={activeIndex}
        onChange={(index) => setActiveIndex(index)}
        swipeable={true}
        emulateTouch={true}
        renderArrowPrev={(onClickHandler, hasPrev) => (
          <IconButton
            onClick={onClickHandler}
            sx={{
              position: "absolute",
              top: "50%",
              left: esMobile ? "10px" : "30px",
              transform: "translateY(-50%)",
              color: "white",
              backgroundColor: "rgba(0, 0, 0, 0.3)",
              backdropFilter: "blur(8px)",
              padding: esMobile ? "8px" : "12px",
              zIndex: 10,
              transition: "all 0.3s ease",
              border: "1px solid rgba(255, 255, 255, 0.1)",
              "&:hover": {
                backgroundColor: "rgba(255, 199, 9, 0.9)",
                color: "#000",
                transform: "translateY(-50%) scale(1.1)",
              },
            }}
          >
            <NavigateBeforeIcon sx={{ fontSize: esMobile ? 28 : 36 }} />
          </IconButton>
        )}
        renderArrowNext={(onClickHandler, hasNext) => (
          <IconButton
            onClick={onClickHandler}
            sx={{
              position: "absolute",
              top: "50%",
              right: esMobile ? "10px" : "30px",
              transform: "translateY(-50%)",
              color: "white",
              backgroundColor: "rgba(0, 0, 0, 0.3)",
              backdropFilter: "blur(8px)",
              padding: esMobile ? "8px" : "12px",
              zIndex: 10,
              transition: "all 0.3s ease",
              border: "1px solid rgba(255, 255, 255, 0.1)",
              "&:hover": {
                backgroundColor: "rgba(255, 199, 9, 0.9)",
                color: "#000",
                transform: "translateY(-50%) scale(1.1)",
              },
            }}
          >
            <NavigateNextIcon sx={{ fontSize: esMobile ? 28 : 36 }} />
          </IconButton>
        )}
      >
        {carouselImages.map((texture, index) => (
          <Box
            key={`carousel-image-${index}`}
            sx={{
              position: "relative",
              height: esMobile ? "500px" : "700px",
              overflow: "hidden",
            }}
          >
            {/* Imagen con overlay gradiente */}
            <Box
              sx={{
                position: "absolute",
                inset: 0,
                "&::before": {
                  content: '""',
                  position: "absolute",
                  inset: 0,
                  background:
                    "linear-gradient(to right, rgba(0,0,0,0.75) 0%, rgba(0,0,0,0.3) 50%, rgba(0,0,0,0.1) 100%)",
                  zIndex: 1,
                },
              }}
            >
              <img
                src={texture}
                alt={`carousel-${index}`}
                style={{
                  width: "100%",
                  height: "100%",
                  objectFit: "cover",
                  filter: "brightness(0.85) contrast(1.1)",
                }}
              />
            </Box>

            {/* Contenido de texto */}
            <Box
              sx={{
                position: "absolute",
                top: "50%",
                left: esMobile ? "5%" : "8%",
                transform: "translateY(-50%)",
                zIndex: 2,
                maxWidth: esMobile ? "90%" : "500px",
                opacity: activeIndex === index ? 1 : 0,
                transition: "all 0.6s cubic-bezier(0.4, 0, 0.2, 1)",
                transitionDelay: activeIndex === index ? "0.2s" : "0s",
              }}
            >
              {/* Línea decorativa */}
              <Box
                sx={{
                  width: activeIndex === index ? (esMobile ? "40px" : "60px") : 0,
                  height: "3px",
                  backgroundColor: "#FFC709",
                  mb: 2,
                  transition: "width 0.5s cubic-bezier(0.4, 0, 0.2, 1)",
                  transitionDelay: activeIndex === index ? "0.3s" : "0s",
                }}
              />

              {/* H1 clickable */}
              <Typography
                variant="h1"
                onClick={() => handleGenreClick(genres[index], genreValues[index])}
                sx={{
                  fontWeight: 800,
                  textTransform: "uppercase",
                  fontSize: esMobile ? "2.5rem" : "5rem",
                  mb: 2,
                  color: "white",
                  letterSpacing: "-0.02em",
                  lineHeight: 0.9,
                  textShadow: "2px 2px 20px rgba(0,0,0,0.5)",
                  background: "linear-gradient(135deg, #FFC709 0%, #FFE066 100%)",
                  WebkitBackgroundClip: "text",
                  WebkitTextFillColor: "transparent",
                  backgroundClip: "text",
                  cursor: "pointer",
                  transition: "all 0.3s ease",
                  "&:hover": {
                    transform: "translateY(-2px)",
                    textShadow: "2px 4px 25px rgba(255, 199, 9, 0.5)",
                  },
                }}
              >
                {genres[index]}
              </Typography>

              <Typography
                variant="subtitle1"
                sx={{
                  color: "rgba(255, 255, 255, 0.9)",
                  fontSize: esMobile ? "0.95rem" : "1.25rem",
                  fontWeight: 300,
                  letterSpacing: "0.05em",
                  lineHeight: 1.6,
                  maxWidth: "400px",
                  textShadow: "1px 1px 10px rgba(0,0,0,0.7)",
                }}
              >
                {subTitles[index]}
              </Typography>

              {/* Botón CTA */}
              <Box
                component="button"
                onClick={() => handleGenreClick(genres[index], genreValues[index])}
                sx={{
                  mt: 4,
                  display: "inline-block",
                  padding: esMobile ? "10px 24px" : "12px 32px",
                  backgroundColor: "transparent",
                  border: "2px solid #FFC709",
                  color: "#FFC709",
                  fontWeight: 600,
                  fontSize: esMobile ? "0.85rem" : "0.95rem",
                  textTransform: "uppercase",
                  letterSpacing: "0.1em",
                  cursor: "pointer",
                  transition: "all 0.3s ease",
                  "&:hover": {
                    backgroundColor: "#FFC709",
                    color: "#000",
                    transform: "translateY(-2px)",
                    boxShadow: "0 8px 20px rgba(255, 199, 9, 0.3)",
                  },
                }}
              >
                Explorar colección
              </Box>
            </Box>
          </Box>
        ))}
      </Carousel>

      {/* Indicadores personalizados */}
      <Box
        sx={{
          position: "absolute",
          bottom: esMobile ? "20px" : "40px",
          left: "50%",
          transform: "translateX(-50%)",
          zIndex: 10,
          display: "flex",
          gap: 1.5,
        }}
      >
        {carouselImages.map((_, index) => (
          <Box
            key={index}
            onClick={() => setActiveIndex(index)}
            sx={{
              width: activeIndex === index ? "40px" : "10px",
              height: "10px",
              borderRadius: "5px",
              backgroundColor:
                activeIndex === index ? "#FFC709" : "rgba(255, 255, 255, 0.4)",
              cursor: "pointer",
              transition: "all 0.3s ease",
              "&:hover": {
                backgroundColor:
                  activeIndex === index ? "#FFC709" : "rgba(255, 255, 255, 0.7)",
              },
            }}
          />
        ))}
      </Box>
    </Box>
  );
};

export default MainCarousel;
