import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";
import { Typography, useMediaQuery } from "@mui/material";

const ShoppingList = () => {
  const dispatch = useDispatch();
  const [value, setValue] = useState("all");
  const items = useSelector((state) => state.cart.items);
  const isNonMobile = useMediaQuery("(min-width: 600px)");
  console.log("items", items);

  const handleChange = (event, newValue) => {
    setValue(event.newValue);
  };

  async function getItems() {
    const items = await fetch(
      "http://localhost:1337/api/items?populate=image",
      { method: "GET" }
    );
    const itemsJson = await items.json();
    dispatch(setItems(itemsJson.data));
  }

  useEffect(() => {
    getItems();
  }, []);

  const JazzItems = items.filter((item) => item.attributes.genres === "Jazz");
  const SoulItems = items.filter((item) => item.attributes.genres === "Soul");
  const RockItems = items.filter((item) => item.attributes.genres === "Rock");
  const PopItems = items.filter((item) => item.attributes.genres === "Pop");

  return (
    <Box width="80%" margin="80px auto">
      <Typography variant="h3" textAlign="center">
        More Albums <b>Products</b>
      </Typography>
    </Box>
  );
};

export default ShoppingList;
