import React, { useEffect, useState, useMemo, useCallback } from "react";
import {
  Tabs,
  Tab,
  Box,
  Typography,
  Input,
  useMediaQuery,
  CircularProgress,
  Grid,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";

// API + categorías (ajusta si necesitas otros endpoints/categorías)
const API_URL =
  "https://sprint9-ecommerce-production.up.railway.app/api/items?populate=image&pagination[limit]=50";
const CATEGORIES = [
  { label: "ALL", value: "all" },
  { label: "JAZZ", value: "jazz" },
  { label: "POP", value: "pop" },
  { label: "ROCK", value: "rock" },
  { label: "SOUL", value: "soul" },
];

const BREAKPOINTS = {
  mobile: 600,
  tablet: 960,
};

const ShoppingList = () => {
  const dispatch = useDispatch();
  const isMobile = useMediaQuery(`(max-width:${BREAKPOINTS.mobile}px)`);
  const isTablet = useMediaQuery(`(max-width:${BREAKPOINTS.tablet}px)`);

  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const items = useSelector((state) => state.cart.items || []); // evita undefined
  const [isLoading, setIsLoading] = useState(true);

  const responsiveValues = useMemo(
    () => ({
      containerWidth: isMobile ? "92%" : isTablet ? "88%" : "80%",
      containerMargin: isMobile ? "60px auto" : "80px auto",
      titleVariant: isMobile ? "h5" : "h3",
      searchMargin: isMobile ? "6px" : "12px",
      tabsMargin: isMobile ? "12px" : "24px",
    }),
    [isMobile, isTablet]
  );

  const handleCategoryChange = useCallback((event, newValue) => {
    setSelectedCategory(newValue);
  }, []);

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value);
  }, []);

  const filteredItems = useMemo(() => {
    return items.filter((item) => {
      const matchesCategory =
        selectedCategory === "all" ||
        (item.attributes?.category || "").toLowerCase() === selectedCategory;

      const matchesSearch =
        searchTerm.trim() === "" ||
        (item.attributes?.name || "")
          .toLowerCase()
          .includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  }, [items, selectedCategory, searchTerm]);

  // Escucha eventos externos y sessionStorage como antes
  useEffect(() => {
    const handleExternalCategoryChange = (event) => {
      if (event?.detail?.category) setSelectedCategory(event.detail.category);
    };
    window.addEventListener("categoryChange", handleExternalCategoryChange);

    const savedCategory = sessionStorage.getItem("selectedCategory");
    if (savedCategory && CATEGORIES.some((c) => c.value === savedCategory)) {
      setSelectedCategory(savedCategory);
      sessionStorage.removeItem("selectedCategory");
    }

    return () =>
      window.removeEventListener("categoryChange", handleExternalCategoryChange);
  }, []);

  // Fetch items
  useEffect(() => {
    if (items.length > 0) {
      setIsLoading(false);
      return;
    }

    const fetchItems = async () => {
      setIsLoading(true);
      try {
        const response = await fetch(API_URL);
        if (!response.ok) throw new Error(`HTTP ${response.status}`);
        const data = await response.json();
        if (data?.data && Array.isArray(data.data)) {
          dispatch(setItems(data.data));
        } else {
          dispatch(setItems([]));
          console.error("Unexpected API response:", data);
        }
      } catch (err) {
        console.error("Error fetching items:", err);
        dispatch(setItems([]));
      } finally {
        setIsLoading(false);
      }
    };

    fetchItems();
  }, [dispatch, items.length]);

  const renderContent = () => {
    if (isLoading) {
      return (
        <Box
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: 200,
          }}
        >
          <CircularProgress />
          <Typography sx={{ ml: 2, color: "text.secondary" }}>
            Loading albums...
          </Typography>
        </Box>
      );
    }

    if (filteredItems.length === 0) {
      return (
        <Typography
          variant="h6"
          textAlign="center"
          color="text.secondary"
          sx={{ mt: 4, fontStyle: "italic" }}
        >
          {searchTerm
            ? `No results for "${searchTerm}"`
            : `No albums in the ${selectedCategory} category yet.`}
        </Typography>
      );
    }

    return (
      <Grid
        container
        spacing={3}
        justifyContent="center"
        alignItems="stretch"
        sx={{ p: 1 }}
      >
        {filteredItems.map((item) => (
          <Grid
            item
            key={`item-${item.id}`}
            xs={12} // 1 por fila en xs
            sm={6} // 2 por fila en sm
            md={6} // 2 por fila en md (mantener equilibrio hasta lg)
            lg={4} // 3 por fila a partir de lg
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            {/* Wrapping Box: centra el contenido y fuerza restricciones sobre imágenes hijas */}
            <Box
              sx={{
                width: "100%",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                // limita el ancho de la tarjeta si quieres (opcional)
                // maxWidth: 420,
                // Afecta a cualquier <img> dentro del Item para que no supere 500px de alto y mantenga proporción
                "& img": {
                  maxHeight: { xs: "260px", sm: "350px", md: "420px", lg: "500px" },
                  height: "auto",
                  width: "auto",
                  maxWidth: "100%",
                  objectFit: "contain",
                  display: "block",
                },
              }}
            >
              <Item item={item} />
            </Box>
          </Grid>
        ))}
      </Grid>
    );
  };

  return (
    <Box
      component="section"
      aria-label="Music albums collection"
      width={responsiveValues.containerWidth}
      margin={responsiveValues.containerMargin}
      id="shopping-list"
    >
      <Typography
        variant={responsiveValues.titleVariant}
        component="h2"
        textAlign="center"
        sx={{ mb: 2, fontWeight: 600 }}
      >
        Our Featured{" "}
        <Box component="span" sx={{ color: "primary.main", fontWeight: 700 }}>
          Albums
        </Box>
      </Typography>

      <Input
        value={searchTerm}
        onChange={handleSearchChange}
        placeholder="Search albums..."
        fullWidth
        sx={{
          m: responsiveValues.searchMargin,
          transition: "opacity 0.25s ease-in-out",
          opacity: 0.9,
          "&:focus-within": { opacity: 1 },
        }}
        aria-label="Search albums"
      />

      <Tabs
        value={selectedCategory}
        onChange={handleCategoryChange}
        textColor="primary"
        indicatorColor="primary"
        centered={!isMobile}
        variant={isMobile ? "scrollable" : "standard"}
        scrollButtons={isMobile ? "auto" : "off"}
        sx={{
          m: responsiveValues.tabsMargin,
          "& .MuiTabs-flexContainer": {
            flexWrap: isMobile ? "nowrap" : "wrap",
            justifyContent: "center",
          },
          "& .MuiTab-root": {
            fontSize: isMobile ? "0.8rem" : "0.9rem",
          },
        }}
      >
        {CATEGORIES.map((category) => (
          <Tab
            key={category.value}
            label={category.label}
            value={category.value}
            aria-label={`Show ${category.label.toLowerCase()} albums`}
          />
        ))}
      </Tabs>

      {renderContent()}

      {filteredItems.length > 0 && (
        <Typography
          variant="body2"
          color="text.secondary"
          textAlign="center"
          sx={{ mt: 2 }}
        >
          Showing {filteredItems.length}{" "}
          {filteredItems.length === 1 ? "album" : "albums"}
          {searchTerm && ` for "${searchTerm}"`}
          {selectedCategory !== "all" && ` in the ${selectedCategory} category`}
        </Typography>
      )}
    </Box>
  );
};

export default ShoppingList;
