import React, { useEffect, useState, useMemo, useCallback } from "react";
import { 
  Tabs, 
  Tab, 
  Box, 
  Typography, 
  Input,
  useMediaQuery
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";

// Constants for better maintainability
const API_URL = "https://sprint9-ecommerce-production.up.railway.app/api/items?populate=image&pagination[limit]=50";
const CATEGORIES = [
  { label: "ALL", value: "all" },
  { label: "JAZZ", value: "jazz" },
  { label: "POP", value: "pop" },
  { label: "ROCK", value: "rock" },
  { label: "SOUL", value: "soul" },
];

const ShoppingList = () => {
  const dispatch = useDispatch();
  const isMobile = useMediaQuery("(max-width:600px)");

  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const items = useSelector((state) => state.cart.items);

  // Event handlers
  const handleCategoryChange = useCallback((event, newValue) => {
    setSelectedCategory(newValue);
  }, []);

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value);
  }, []);

  // Filter items based on category and search term
  const filteredItems = useMemo(() => {
    return items.filter(item => {
      const matchesCategory = selectedCategory === "all" || 
        item.attributes?.category?.toLowerCase() === selectedCategory;

      const matchesSearch = searchTerm.trim() === "" ||
        item.attributes?.name?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  }, [items, selectedCategory, searchTerm]);

  // Listen for category changes from MainCarousel
  useEffect(() => {
    const handleExternalCategoryChange = (event) => {
      setSelectedCategory(event.detail.category);
    };

    window.addEventListener('categoryChange', handleExternalCategoryChange);

    // Check for saved category in sessionStorage
    const savedCategory = sessionStorage.getItem('selectedCategory');
    if (savedCategory && CATEGORIES.some(cat => cat.value === savedCategory)) {
      setSelectedCategory(savedCategory);
      sessionStorage.removeItem('selectedCategory');
    }

    return () => {
      window.removeEventListener('categoryChange', handleExternalCategoryChange);
    };
  }, []);

  // Fetch items from API
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await fetch(API_URL, { 
          method: "GET",
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const itemsData = await response.json();

        if (itemsData.data && Array.isArray(itemsData.data)) {
          dispatch(setItems(itemsData.data));
        } else {
          console.error("Unexpected API response structure:", itemsData);
          dispatch(setItems([]));
        }
      } catch (error) {
        console.error("Error fetching items:", error);
        dispatch(setItems([]));
      }
    };

    fetchItems();
  }, [dispatch]);

  // Render loading state
  const renderContent = () => {
    if (items.length === 0) {
      return (
        <Box 
          display="flex" 
          justifyContent="center" 
          alignItems="center" 
          minHeight="200px"
        >
          <Typography 
            variant="h6" 
            textAlign="center" 
            color="text.secondary"
          >
            Loading albums...
          </Typography>
        </Box>
      );
    }

    if (filteredItems.length === 0) {
      return (
        <Box 
          display="flex" 
          justifyContent="center" 
          alignItems="center" 
          minHeight="200px"
        >
          <Typography 
            variant="h6" 
            textAlign="center" 
            color="text.secondary"
          >
            {searchTerm 
              ? `No albums found for "${searchTerm}" in ${selectedCategory === 'all' ? 'any category' : selectedCategory}`
              : `No albums found in ${selectedCategory} category`
            }
          </Typography>
        </Box>
      );
    }

    return (
      <Box
        display="grid"
        gridTemplateColumns={{
          xs: "1fr", // Mobile: 1 column
          sm: "repeat(2, 1fr)", // Tablet: 2 columns
          md: "repeat(3, 1fr)", // Desktop: 3 columns
        }}
        gap="24px" // Consistent spacing
        justifyContent="center"
        justifyItems="center" // Center items in their grid cells
        width="100%"
        margin="0 auto"
        padding="20px 0"
      >
        {filteredItems.map((item) => (
          <Item
            key={`item-${item.id}-${item.attributes?.name}`}
            item={item}
            style={{ 
              width: "100%",
              maxWidth: "350px", // Consistent max width
            }}
          />
        ))}
      </Box>
    );
  };

  return (
    <Box 
      component="section"
      aria-label="Music albums collection"
      width={{ xs: "90%", sm: "85%", md: "80%" }}
      margin={{ xs: "60px auto", sm: "80px auto" }}
      id="shopping-list"
      display="flex"
      flexDirection="column"
      alignItems="center" // Center everything horizontally
    >
      {/* Header - Always centered */}
      <Typography 
        variant={{ xs: "h4", sm: "h3" }}
        component="h2"
        textAlign="center"
        sx={{ 
          mb: 3,
          fontWeight: 600,
          width: "100%"
        }}
      >
        Our Featured <Box component="span" sx={{ color: "primary.main", fontWeight: 700 }}>Albums</Box>
      </Typography>

      {/* Search Input - Always centered */}
      <Box width="100%" display="flex" justifyContent="center">
        <Input
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search albums by name..."
          sx={{
            width: { xs: "100%", sm: "80%", md: "60%" },
            transition: "all 0.3s ease-in-out",
            transform: searchTerm ? "translateX(0)" : "translateX(-50px)",
            opacity: searchTerm ? 1 : 0.7,
            '&:focus-within': {
              transform: "translateX(0)",
              opacity: 1,
            },
            mb: 2
          }}
          aria-label="Search albums"
        />
      </Box>

      {/* Category Tabs - Always centered */}
      <Box width="100%" display="flex" justifyContent="center">
        <Tabs
          value={selectedCategory}
          onChange={handleCategoryChange}
          textColor="primary"
          indicatorColor="primary"
          centered
          variant={isMobile ? "scrollable" : "standard"}
          scrollButtons={isMobile ? "auto" : false}
          sx={{
            mb: 3,
            '& .MuiTabs-flexContainer': {
              justifyContent: "center",
            },
            '& .MuiTab-root': {
              fontSize: { xs: "0.8rem", sm: "0.9rem" },
              minWidth: { xs: "auto", sm: "unset" },
              px: { xs: 2, sm: 3 },
            },
          }}
        >
          {CATEGORIES.map((category) => (
            <Tab 
              key={category.value}
              label={category.label}
              value={category.value}
              aria-label={`Show ${category.label.toLowerCase()} albums`}
            />
          ))}
        </Tabs>
      </Box>

      {/* Items Grid */}
      {renderContent()}

      {/* Results Count - Always centered */}
      {filteredItems.length > 0 && (
        <Typography 
          variant="body2" 
          color="text.secondary" 
          textAlign="center"
          sx={{ mt: 4, width: "100%" }}
        >
          Showing {filteredItems.length} of {items.length} albums
          {searchTerm && ` for "${searchTerm}"`}
          {selectedCategory !== 'all' && ` in ${selectedCategory}`}
        </Typography>
      )}
    </Box>
  );
};

export default ShoppingList;
