import { useDispatch, useSelector } from "react-redux";
import { Badge, Box, IconButton } from "@mui/material";
import {
  PersonOutline,
  ShoppingBagOutlined,
  MenuOutlined,
  SearchOutlined,
} from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { shades } from "../../theme";
import { setIsCartOpen } from "../../state";
import logo from "../../assets/logo/logo.png";
import { useState } from "react";
import SignupForm from "../global/SignUpForm";


function Navbar() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [isFormOpen, setIsFormOpen] = useState(false);

  const handlePersonClick = () => {
    setIsFormOpen(true);
  };


  return (
    <Box
      display="flex"
      alignItems="center"
      width="100%"
      height="60px"
      backgroundColor="#2D2C2F"
      color="black"
      position="fixed"
      top="0"
      left="0"
      zIndex="1"
    >
      <Box
        width="80%"
        margin="auto"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
      >
        <Box
          onClick={() => navigate("/")}
          sx={{ "&:hover": { cursor: "pointer" } }}
          color={shades.secondary[500]}
          display="flex"
          alignItems="center"
        >
          <img
            src={logo}
            alt="Logo"
            style={{ marginRight: "8px", height: "35px", width: "auto" }}
          />{" "}
        </Box>
        <Box
          display="flex"
          justifyContent="space-between"
          columnGap="20px"
          zIndex="2"
        >
          <IconButton sx={{ color: "#FFC709" }}>
            <SearchOutlined />
          </IconButton>
          <IconButton onClick={handlePersonClick} sx={{ color: "#FFC709" }}>
        <PersonOutline />
      </IconButton>
          <Badge
            badgeContent={cart.length}
            color="secondary"
            invisible={cart.length === 0}
            sx={{
              "& .MuiBadge-badge": {
                right: 5,
                top: 5,
                padding: "0 4px",
                height: "14px",
                minWidth: "13px",
              },
            }}
          >
            <IconButton
              onClick={() => dispatch(setIsCartOpen({}))}
              sx={{ color: "#FFC709" }}
            >
              <ShoppingBagOutlined />
            </IconButton>
          </Badge>
          <IconButton sx={{ color: "#FFC709" }}>
            <MenuOutlined />
          </IconButton>{isFormOpen && <SignupForm handleClose={() => setIsFormOpen(false)} />}
        </Box>
      </Box>
    </Box>
  );
}

export default Navbar;
