import React, { useState, useCallback } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography, Button } from "@mui/material";
import { shades } from "../theme";
import { addToCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [isHovered, setIsHovered] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  // Loading/Error placeholder
  if (!item?.attributes) {
    return (
      <Box sx={{ maxWidth: { xs: '100%', sm: '400px', md: '500px' }, margin: '0 auto' }}>
        <Box
          sx={{
            backgroundColor: shades.neutral[200],
            aspectRatio: '1 / 1',
            width: '100%',
            borderRadius: '4px',
            animation: 'pulse 1.5s ease-in-out infinite',
            '@keyframes pulse': {
              '0%, 100%': { opacity: 1 },
              '50%': { opacity: 0.6 },
            },
          }}
        />
        <Box mt={2}>
          <Box sx={{ backgroundColor: shades.neutral[200], height: '16px', mb: 1, borderRadius: '4px', width: '40%' }} />
          <Box sx={{ backgroundColor: shades.neutral[200], height: '20px', mb: 0.5, borderRadius: '4px', width: '85%' }} />
          <Box sx={{ backgroundColor: shades.neutral[200], height: '20px', borderRadius: '4px', width: '30%', mt: 1 }} />
        </Box>
      </Box>
    );
  }

  const { 
    category = "Unknown",
    price = 0,
    name = "Unnamed Item",
    image,
  } = item.attributes;

  const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);

  const imageUrl = image?.data?.attributes?.formats?.medium?.url 
    ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
    : "/default-image.jpg";

  const handleAddToCart = useCallback((e) => {
    e.stopPropagation();
    if (!isItemInCart) {
      dispatch(addToCart({ item: { ...item, count: 1 } }));
    }
  }, [isItemInCart, dispatch, item]);

  const handleNavigate = useCallback(() => {
    navigate(`/item/${item.id}`);
  }, [navigate, item.id]);

  const formatCategory = (cat) => {
    return cat
      .replace(/([A-Z])/g, " $1")
      .replace(/^./, (str) => str.toUpperCase())
      .trim();
  };

  return (
    <Box
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={handleNavigate}
      sx={{ 
        cursor: 'pointer', 
        position: 'relative',
        maxWidth: { xs: '100%', sm: '400px', md: '500px' },
        margin: '0 auto',
        transition: 'transform 0.2s ease-out',
        '&:active': {
          transform: 'scale(0.98)',
        }
      }}
    >
      {/* Image Container */}
      <Box
        sx={{
          position: 'relative',
          overflow: 'hidden',
          borderRadius: '4px',
          backgroundColor: shades.neutral[100],
          aspectRatio: '1 / 1',
          boxShadow: isHovered 
            ? '0 8px 24px rgba(0, 0, 0, 0.12)' 
            : '0 2px 8px rgba(0, 0, 0, 0.08)',
          transition: 'box-shadow 0.3s ease',
        }}
      >
        <Box
          component="img"
          alt={name}
          src={imageUrl}
          onLoad={() => setImageLoaded(true)}
          sx={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            transition: 'transform 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
            transform: isHovered ? 'scale(1.05)' : 'scale(1)',
            opacity: imageLoaded ? 1 : 0,
          }}
        />

        {/* Overlay gradiente en hover */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'linear-gradient(to top, rgba(0,0,0,0.4) 0%, transparent 50%)',
            opacity: isHovered ? 1 : 0,
            transition: 'opacity 0.3s ease',
            pointerEvents: 'none',
          }}
        />

        {/* Button Container */}
        <Box
          sx={{
            position: 'absolute',
            bottom: 0,
            left: 0,
            right: 0,
            display: 'flex',
            justifyContent: 'center',
            padding: { xs: '12px', sm: '16px' },
            opacity: isHovered ? 1 : 0,
            transform: isHovered ? 'translateY(0)' : 'translateY(10px)',
            transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
          }}
        >
          <Button
            onClick={handleAddToCart}
            disabled={isItemInCart}
            sx={{
              backgroundColor: isItemInCart ? shades.neutral[400] : shades.primary[500],
              color: "white",
              fontSize: { xs: '0.75rem', sm: '0.875rem' },
              fontWeight: 600,
              letterSpacing: '0.5px',
              padding: { xs: '8px 20px', sm: '10px 28px' },
              borderRadius: '4px',
              boxShadow: '0 4px 14px rgba(0, 0, 0, 0.25)',
              transition: 'all 0.2s ease',
              textTransform: 'uppercase',
              cursor: isItemInCart ? 'not-allowed' : 'pointer',
              '&:hover': {
                backgroundColor: isItemInCart ? shades.neutral[400] : shades.primary[400],
                transform: isItemInCart ? 'none' : 'translateY(-2px)',
                boxShadow: isItemInCart ? '0 4px 14px rgba(0, 0, 0, 0.25)' : '0 6px 20px rgba(0, 0, 0, 0.3)',
              },
              '&:active': {
                transform: isItemInCart ? 'none' : 'translateY(0)',
              }
            }}
          >
            {isItemInCart ? "In Cart" : "Add to Cart"}
          </Button>
        </Box>
      </Box>

      {/* Product Info */}
      <Box 
        mt={{ xs: 1.5, sm: 2 }}
        px={{ xs: 0.5, sm: 0 }}
      >
        <Typography 
          variant="caption"
          sx={{
            color: shades.neutral[500],
            textTransform: 'uppercase',
            letterSpacing: '1px',
            fontSize: { xs: '0.65rem', sm: '0.7rem' },
            fontWeight: 500,
            display: 'block',
            mb: 0.5,
          }}
        >
          {formatCategory(category)}
        </Typography>

        <Typography 
          variant="body1"
          sx={{
            fontWeight: 500,
            fontSize: { xs: '0.9rem', sm: '1rem' },
            color: shades.neutral[900],
            mb: 0.5,
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            display: '-webkit-box',
            WebkitLineClamp: 2,
            WebkitBoxOrient: 'vertical',
            lineHeight: 1.4,
            minHeight: { xs: '2.5rem', sm: '2.8rem' },
          }}
        >
          {name}
        </Typography>

        <Typography 
          variant="h6"
          sx={{
            fontWeight: 700,
            fontSize: { xs: '1rem', sm: '1.1rem' },
            color: shades.primary[500],
            mt: 0.5,
          }}
        >
          â‚¬ {price.toFixed(2)}
        </Typography>
      </Box>
    </Box>
  );
};

export default Item;
