import React, { useState, useCallback, useMemo } from "react";
import { Box, Typography } from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [imageLoaded, setImageLoaded] = useState(false);
  const [isHovered, setIsHovered] = useState(false);

  const handleAddToCart = useCallback(
    (e) => {
      e.stopPropagation();
      if (item?.attributes) {
        const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
        if (!isItemInCart) {
          dispatch(addToCart({ item: { ...item, count: 1 } }));
        }
      }
    },
    [cart, dispatch, item]
  );

  const handleNavigate = useCallback(() => {
    if (item?.id) {
      navigate(`/item/${item.id}`);
    }
  }, [navigate, item]);

  const { itemData, isItemInCart, imageUrl } = useMemo(() => {
    if (!item?.attributes) return { itemData: null, isItemInCart: false, imageUrl: "" };

    const { category = "Unknown", price = 0, name = "Unnamed Item", image } = item.attributes;

    const imageUrl = image?.data?.attributes?.formats?.medium?.url
      ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
      : "/default-image.jpg";

    const isInCart = cart.some((cartItem) => cartItem.id === item.id);

    return {
      itemData: { category, price, name },
      isItemInCart: isInCart,
      imageUrl,
    };
  }, [item, cart]);

  if (!item?.attributes) {
    return (
      <Box sx={{ width: "100%", height: "100%", backgroundColor: "#f8f8f8" }} />
    );
  }

  const { category, price, name } = itemData;

  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      height: "100%", // para que ocupe toda la altura del Grid
      cursor: "pointer",
      transition: "all 0.3s ease",
    },
    imageContainer: {
      aspectRatio: "1 / 1",
      overflow: "hidden",
      mb: 2,
      position: "relative",
      backgroundColor: "#f8f8f8",
    },
    image: {
      width: "100%",
      height: "100%",
      objectFit: "cover",
      opacity: imageLoaded ? 1 : 0,
      transition: "all 0.6s cubic-bezier(0.16, 1, 0.3, 1)",
      filter: isHovered ? "grayscale(100%)" : "grayscale(0%)",
    },
    cartStatus: {
      position: "absolute",
      bottom: 8,
      left: 8,
      fontSize: 11,
      color: "#fff",
      backgroundColor: "rgba(0,0,0,0.7)",
      padding: "4px 8px",
      textTransform: "uppercase",
      opacity: isItemInCart ? 1 : 0,
      transition: "opacity 0.3s ease",
    },
    contentContainer: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between",
      flexGrow: 1,
    },
    category: { fontSize: 11, color: "#888", textTransform: "uppercase", mb: 0.5 },
    name: { fontSize: 14, color: "#000", lineHeight: 1.4, mb: 1 },
    bottomRow: { display: "flex", justifyContent: "space-between", alignItems: "center" },
    price: { fontSize: 14, color: "#000" },
    addButton: {
      fontSize: 11,
      textTransform: "uppercase",
      border: "none",
      background: "transparent",
      cursor: "pointer",
      opacity: isItemInCart ? 0 : 1,
      position: "relative",
      "&::after": {
        content: '""',
        position: "absolute",
        bottom: -2,
        left: 0,
        width: "0%",
        height: "1px",
        backgroundColor: "#000",
        transition: "width 0.3s ease",
      },
      "&:hover::after": { width: "100%" },
    },
  };

  return (
    <Box
      sx={styles.container}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={handleNavigate}
    >
      <Box sx={styles.imageContainer}>
        <Box
          component="img"
          src={imageUrl}
          alt={name}
          onLoad={() => setImageLoaded(true)}
          sx={styles.image}
        />
        {isItemInCart && <Box sx={styles.cartStatus}>In Cart</Box>}
      </Box>

      <Box sx={styles.contentContainer}>
        <Box>
          <Typography sx={styles.category}>{category}</Typography>
          <Typography sx={styles.name}>{name}</Typography>
        </Box>
        <Box sx={styles.bottomRow}>
          <Typography sx={styles.price}>€{price.toFixed(2)}</Typography>
          {!isItemInCart && (
            <Box component="button" sx={styles.addButton} onClick={handleAddToCart}>
              Add to Cart
            </Box>
          )}
        </Box>
      </Box>
    </Box>
  );
};

export default Item;
