import { Box, Button, Typography } from "@mui/material";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import Item from "../../components/Item";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import { addToCart } from "../../state";
import { useDispatch } from "react-redux";

const ItemDetails = () => {
  const dispatch = useDispatch();
  const { itemId } = useParams();
  const [value, setValue] = useState("description");
  const [count, setCount] = useState(1);
  const [items, setItems] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0); // Nuevo estado para el índice actual

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  async function getItems() {
    const items = await fetch(
      `http://localhost:1337/api/items?populate=image`,
      {
        method: "GET",
      }
    );
    const itemsJson = await items.json();
    setItems(itemsJson.data);
  }

  useEffect(() => {
    getItems();
  }, []); // Solo carga una vez al montar el componente

  // Después de obtener el artículo actual y la lista de artículos
  const relatedItems = items.filter((relatedItem) => {
    return (
      items[currentIndex] &&
      items[currentIndex].attributes &&
      items[currentIndex].attributes.category &&
      relatedItem.attributes.category ===
        items[currentIndex].attributes.category &&
      relatedItem.id !== items[currentIndex].id
    );
  });

  const handlePrev = () => {
    setCurrentIndex((prevIndex) => Math.max(prevIndex - 1, 0));
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => Math.min(prevIndex + 1, items.length - 1));
  };

  return (
    <Box width="80%" m="80px auto">
      {items[currentIndex] && (
        <Box display="flex" flexWrap="wrap" columnGap="40px">
          {/* Resto del código... */}
        </Box>
      )}

      {/* Resto del código... */}

      {/* PREV Y NEXT BUTTONS */}
      <Box display="flex" justifyContent="space-between" mt="20px">
        <Button
          disabled={currentIndex === 0}
          variant="outlined"
          onClick={handlePrev}
        >
          Prev
        </Button>
        <Button
          disabled={currentIndex === items.length - 1}
          variant="outlined"
          onClick={handleNext}
        >
          Next
        </Button>
      </Box>

      {/* RELATED ITEMS */}
      {relatedItems.length > 0 && (
        <Box mt="50px" width="100%">
          <Typography variant="h3" fontWeight="bold">
            Related Products
          </Typography>
          <Box
            mt="20px"
            display="flex"
            flexWrap="wrap"
            columnGap="1.33%"
            justifyContent="space-between"
          >
            {relatedItems.slice(0, 4).map((item, i) => (
              <Item key={`${item.attributes.name}-${i}`} item={item} />
            ))}
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default ItemDetails;
