import { Box, Button, Typography } from "@mui/material";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import Item from "../../components/Item";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import { addToCart } from "../../state";
import { useDispatch } from "react-redux";

const ItemDetails = () => {
  const dispatch = useDispatch();
  const { itemId } = useParams();
  const [value, setValue] = useState("description");
  const [item, setItem] = useState(null);
  const [items, setItems] = useState([]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  async function getItem() {
    const item = await fetch(
      `http://localhost:1337/api/items/${itemId}?populate=image`,
      {
        method: "GET",
      }
    );
    const itemJson = await item.json();
    setItem(itemJson.data);
  }

  async function getItems() {
    const items = await fetch(
      `http://localhost:1337/api/items?populate=image`,
      {
        method: "GET",
      }
    );
    const itemsJson = await items.json();
    setItems(itemsJson.data);
  }

  useEffect(() => {
    getItem();
    getItems();
  }, [itemId]); // eslint-disable-line react-hooks/exhaustive-deps

  // Después de obtener el artículo actual y la lista de artículos
  const relatedItems = items.filter((relatedItem) => {
    return (
      item &&
      item.attributes &&
      item.attributes.category &&
      relatedItem.attributes.category === item.attributes.category &&
      relatedItem.id !== item.id // Añadido para evitar repetir el mismo artículo
    );
  });

  return (
    <Box width="80%" m="80px auto">
      {item && (
        <Box display="flex" flexWrap="wrap" columnGap="40px">
          {/* IMAGES */}
          <Box flex="1 1 40%" mb="40px">
            <img
              alt={item.attributes.name}
              width="100%"
              height="100%"
              src={`http://localhost:1337${item.attributes.image.data.attributes.formats.medium.url}`}
              style={{ objectFit: "contain" }}
            />
          </Box>

          {/* ACTIONS */}
          <Box flex="1 1 50%" mb="40px">
            <Box display="flex" justifyContent="space-between">
              <Box>Home/Item</Box>
              <Box>Prev Next</Box>
            </Box>

            <Box m="65px 0 25px 0">
              <Typography variant="h3">{item.attributes.name}</Typography>
              <Typography>€ {item.attributes.price}</Typography>
              {item.attributes.mediaCondition && (
                <Typography>
                  <strong>Media Condition:</strong>{" "}
                  {item.attributes.mediaCondition}
                </Typography>
              )}
              {item.attributes.sleeveCondition && (
                <Typography>
                  <strong>Sleeve Condition:</strong>{" "}
                  {item.attributes.sleeveCondition}
                </Typography>
              )}
              {item.attributes.tracklist && (
                <Typography>
                  <strong>Tracklist:</strong> {item.attributes.tracklist}
                </Typography>
              )}
              <Typography sx={{ mt: "20px" }}>
                <ul style={{ listStyleType: "none", padding: 0 }}>
                  {item.attributes.longDescription &&
                    item.attributes.longDescription
                      .split("\n")
                      .map((item, i) => <li key={i}>{item}</li>)}
                </ul>
              </Typography>
            </Box>

            <Box display="flex" alignItems="center" minHeight="50px">
              <Button
                sx={{
                  backgroundColor: "#222222",
                  color: "white",
                  borderRadius: 0,
                  minWidth: "150px",
                  padding: "10px 40px",
                }}
                onClick={() => dispatch(addToCart({ item: { ...item } }))}
              >
                ADD TO CART
              </Button>
            </Box>
            <Box>
              <Box m="20px 0 5px 0" display="flex">
                <FavoriteBorderOutlinedIcon />
                <Typography sx={{ ml: "5px" }}>ADD TO WISHLIST</Typography>
              </Box>
              <Typography>CATEGORIES: {item.attributes.category}</Typography>
            </Box>
          </Box>
        </Box>
      )}

      {/* INFORMATION */}
      <Box m="20px 0">
        <Tabs value={value} onChange={handleChange}>
          <Tab label="DESCRIPTION" value="description" />
          <Tab label="REVIEWS" value="reviews" />
        </Tabs>
      </Box>
      <Box display="flex" flexWrap="wrap" gap="15px">
        {value === "description" && (
          <div>
            {item &&
              item.attributes.shortDescription &&
              item.attributes.shortDescription
                .split("\n")
                .map((item, i) => <li key={i}>{item}</li>)}
          </div>
        )}
        {value === "reviews" && <div>reviews</div>}
      </Box>

      {/* RELATED ITEMS */}
      {relatedItems.length > 0 && (
        <Box mt="50px" width="100%">
          <Typography variant="h3" fontWeight="bold">
            Related Products
          </Typography>
          <Box
            mt="20px"
            display="flex"
            flexWrap="wrap"
            columnGap="1.33%"
            justifyContent="space-between"
          >
            {relatedItems.slice(0, 4).map((item, i) => (
              <Item key={`${item.attributes.name}-${i}`} item={item} />
            ))}
          </Box>
        </Box>
      )}
    </Box>
  );
};

export default ItemDetails;
