import React, { useState, useCallback, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography, Button } from "@mui/material";
import { shades } from "../theme";
import { addToCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item, style }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [isHovered, setIsHovered] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  // Memoized values and handlers
  const handleAddToCart = useCallback((e) => {
    e.stopPropagation();
    if (item?.attributes) {
      const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
      if (!isItemInCart) {
        dispatch(addToCart({ item: { ...item, count: 1 } }));
      }
    }
  }, [cart, dispatch, item]);

  const handleNavigate = useCallback(() => {
    if (item?.id) {
      navigate(`/item/${item.id}`);
    }
  }, [navigate, item]);

  // Extract and memoize item data
  const { itemData, isItemInCart, imageUrl } = useMemo(() => {
    if (!item?.attributes) {
      return { itemData: null, isItemInCart: false, imageUrl: "" };
    }

    const { category = "Unknown", price = 0, name = "Unnamed Item", image } = item.attributes;

    const imageUrl = image?.data?.attributes?.formats?.medium?.url 
      ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
      : "/default-image.jpg";

    const isInCart = cart.some((cartItem) => cartItem.id === item.id);

    return {
      itemData: { category, price, name },
      isItemInCart: isInCart,
      imageUrl
    };
  }, [item, cart]);

  // Format category name
  const formatCategory = useCallback((category) => {
    return category
      .replace(/([A-Z])/g, " $1")
      .replace(/^./, (str) => str.toUpperCase())
      .trim();
  }, []);

  // Loading/Error placeholder
  if (!item?.attributes) {
    return (
      <Box 
        sx={{ 
          maxWidth: { xs: '100%', sm: '400px' }, 
          margin: '0 auto',
          ...style 
        }}
      >
        <Box
          sx={{
            backgroundColor: shades.neutral[200],
            aspectRatio: '1 / 1',
            width: '100%',
            borderRadius: '8px',
            animation: 'pulse 1.5s ease-in-out infinite',
            '@keyframes pulse': {
              '0%, 100%': { opacity: 1 },
              '50%': { opacity: 0.6 },
            },
          }}
        />
        <Box mt={2}>
          <Box sx={{ 
            backgroundColor: shades.neutral[200], 
            height: '16px', 
            mb: 1, 
            borderRadius: '4px', 
            width: '40%' 
          }} />
          <Box sx={{ 
            backgroundColor: shades.neutral[200], 
            height: '20px', 
            mb: 0.5, 
            borderRadius: '4px', 
            width: '85%' 
          }} />
          <Box sx={{ 
            backgroundColor: shades.neutral[200], 
            height: '20px', 
            borderRadius: '4px', 
            width: '30%', 
            mt: 1 
          }} />
        </Box>
      </Box>
    );
  }

  const { category, price, name } = itemData;

  // Styles
  const styles = {
    container: {
      cursor: 'pointer',
      position: 'relative',
      maxWidth: { xs: '100%', sm: '400px' },
      margin: '0 auto',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      '&:hover': {
        transform: 'translateY(-4px)',
      },
      '&:active': {
        transform: 'translateY(-2px) scale(0.98)',
      },
      ...style
    },
    imageContainer: {
      position: 'relative',
      overflow: 'hidden',
      borderRadius: '12px',
      backgroundColor: shades.neutral[100],
      aspectRatio: '1 / 1',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      '&:hover': {
        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.12)',
      }
    },
    image: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
      transition: 'transform 0.5s cubic-bezier(0.4, 0, 0.2, 1)',
      transform: isHovered ? 'scale(1.08)' : 'scale(1)',
      opacity: imageLoaded ? 1 : 0,
      filter: isHovered ? 'brightness(1.05)' : 'brightness(1)',
    },
    gradientOverlay: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'linear-gradient(135deg, rgba(255,199,9,0.1) 0%, transparent 50%)',
      opacity: isHovered ? 1 : 0,
      transition: 'opacity 0.3s ease',
      pointerEvents: 'none',
    },
    buttonContainer: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      right: 0,
      display: 'flex',
      justifyContent: 'center',
      padding: { xs: '12px', sm: '16px' },
      opacity: isHovered ? 1 : 0,
      transform: isHovered ? 'translateY(0)' : 'translateY(20px)',
      transition: 'all 0.4s cubic-bezier(0.4, 0, 0.2, 1)',
    },
    button: {
      backgroundColor: isItemInCart ? shades.neutral[400] : shades.primary[500],
      color: "white",
      fontSize: { xs: '0.75rem', sm: '0.875rem' },
      fontWeight: 600,
      letterSpacing: '0.5px',
      padding: { xs: '10px 24px', sm: '12px 32px' },
      borderRadius: '8px',
      boxShadow: '0 4px 16px rgba(0, 0, 0, 0.2)',
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      textTransform: 'uppercase',
      cursor: isItemInCart ? 'not-allowed' : 'pointer',
      minWidth: '120px',
      '&:hover': {
        backgroundColor: isItemInCart ? shades.neutral[400] : shades.primary[400],
        transform: isItemInCart ? 'none' : 'translateY(-2px) scale(1.05)',
        boxShadow: isItemInCart 
          ? '0 4px 16px rgba(0, 0, 0, 0.2)' 
          : '0 8px 24px rgba(0, 0, 0, 0.3)',
      },
      '&:active': {
        transform: isItemInCart ? 'none' : 'translateY(0) scale(0.98)',
      }
    },
    category: {
      color: shades.neutral[500],
      textTransform: 'uppercase',
      letterSpacing: '1px',
      fontSize: { xs: '0.65rem', sm: '0.7rem' },
      fontWeight: 500,
      display: 'block',
      mb: 0.5,
    },
    name: {
      fontWeight: 600,
      fontSize: { xs: '0.9rem', sm: '1rem' },
      color: shades.neutral[900],
      mb: 0.5,
      overflow: 'hidden',
      textOverflow: 'ellipsis',
      display: '-webkit-box',
      WebkitLineClamp: 2,
      WebkitBoxOrient: 'vertical',
      lineHeight: 1.4,
      minHeight: { xs: '2.5rem', sm: '2.8rem' },
    },
    price: {
      fontWeight: 700,
      fontSize: { xs: '1rem', sm: '1.1rem' },
      color: shades.primary[500],
      mt: 0.5,
    }
  };

  return (
    <Box
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      onClick={handleNavigate}
      sx={styles.container}
    >
      {/* Image Container */}
      <Box sx={styles.imageContainer}>
        <Box
          component="img"
          alt={name}
          src={imageUrl}
          onLoad={() => setImageLoaded(true)}
          sx={styles.image}
        />

        {/* Gradient Overlay */}
        <Box sx={styles.gradientOverlay} />

        {/* Add to Cart Button */}
        <Box sx={styles.buttonContainer}>
          <Button
            onClick={handleAddToCart}
            disabled={isItemInCart}
            sx={styles.button}
          >
            {isItemInCart ? "In Cart" : "Add to Cart"}
          </Button>
        </Box>
      </Box>

      {/* Product Info */}
      <Box 
        mt={{ xs: 1.5, sm: 2 }}
        px={{ xs: 0.5, sm: 0 }}
      >
        <Typography sx={styles.category}>
          {formatCategory(category)}
        </Typography>

        <Typography sx={styles.name}>
          {name}
        </Typography>

        <Typography sx={styles.price}>
          € {price.toFixed(2)}
        </Typography>
      </Box>
    </Box>
  );
};

export default Item;
