import React from "react";
import { Box, Typography, IconButton } from "@mui/material";
import { Twitter, Instagram } from "@mui/icons-material";
import { useTheme } from "@emotion/react";
import logo from "../../assets/logo/logo-imagen.png";
import { Link } from "react-router-dom";

function Footer() {
  const theme = useTheme();
  const linkColor = "#FFC709"; // amarillo de marca
  const gradientColor1 = "#FFC709"; // amarillo
  const gradientColor2 = "#fff"; // blanco para contraste

  return (
    <Box
      height="200px"
      backgroundColor="#2D2C2F"
      color={theme.palette.primary.main}
      display="flex"
      justifyContent="center"
      alignItems="center"
    >
      <Box
        width="80%"
        display="flex"
        justifyContent="space-between"
        alignItems="center"
      >
        <Box display="flex" alignItems="center">
          <img
            src={logo}
            alt="Logo"
            style={{ height: "150px", width: "auto" }}
          />
        </Box>

        <Box
          display="flex"
          flexDirection="column"
          alignItems="flex-end"
          justifyContent="center"
          rowGap="10px"
        >
          {["About Us", "Contact Us", "Grading", "Shipping Terms"].map(
            (item, index) => {
              const isLink = item === "About Us";

              const content = (
                <Typography
                  variant="h4"
                  fontWeight="bold"
                  fontSize="1.2em"
                  sx={{
                    color: linkColor,
                    position: "relative",
                    cursor: isLink ? "pointer" : "default",
                    overflow: "hidden",
                    background: `linear-gradient(270deg, ${gradientColor1}, ${gradientColor2}, ${gradientColor1})`,
                    backgroundSize: "600% 100%",
                    WebkitBackgroundClip: "text",
                    WebkitTextFillColor: "transparent",
                    transition: "background-position 2s ease, transform 0.3s ease",
                    "&:hover": isLink
                      ? {
                          backgroundPosition: "100% 0",
                          transform: "translateY(-3px)",
                        }
                      : {},
                  }}
                >
                  {item}
                </Typography>
              );

              if (isLink) {
                return (
                  <Link
                    key={index}
                    to="/about-us"
                    style={{ textDecoration: "none" }}
                  >
                    {content}
                  </Link>
                );
              }
              return <Box key={index}>{content}</Box>;
            }
          )}

          <Box display="flex" columnGap="10px">
            {[
              {
                icon: <Twitter />,
                link: "https://twitter.com/allmyrecords",
              },
              {
                icon: <Instagram />,
                link: "https://www.instagram.com/allmyrecords/",
              },
            ].map((item, index) => (
              <IconButton
                key={index}
                href={item.link}
                target="_blank"
                rel="noopener noreferrer"
                sx={{ color: linkColor }}
              >
                {item.icon}
              </IconButton>
            ))}
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default Footer;
