import React, { useEffect, useState, useMemo, useCallback } from "react";
import { 
  Tabs, 
  Tab, 
  Box, 
  Typography, 
  Input,
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";

const API_URL = "https://sprint9-ecommerce-production.up.railway.app/api/items?populate=image&pagination[limit]=50";

const CATEGORIES = [
  { label: "ALL", value: "all" },
  { label: "JAZZ", value: "jazz" },
  { label: "POP", value: "pop" },
  { label: "ROCK", value: "rock" },
  { label: "SOUL", value: "soul" },
];

const ShoppingList = () => {
  const dispatch = useDispatch();
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const items = useSelector((state) => state.cart.items);

  const handleCategoryChange = useCallback((event, newValue) => {
    setSelectedCategory(newValue);
  }, []);

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value);
  }, []);

  const filteredItems = useMemo(() => {
    return items.filter(item => {
      const matchesCategory = selectedCategory === "all" || 
        item.attributes?.category?.toLowerCase() === selectedCategory;

      const matchesSearch = searchTerm.trim() === "" ||
        item.attributes?.name?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  }, [items, selectedCategory, searchTerm]);

  useEffect(() => {
    const handleExternalCategoryChange = (event) => {
      setSelectedCategory(event.detail.category);
    };

    window.addEventListener('categoryChange', handleExternalCategoryChange);

    const savedCategory = sessionStorage.getItem('selectedCategory');
    if (savedCategory && CATEGORIES.some(cat => cat.value === savedCategory)) {
      setSelectedCategory(savedCategory);
      sessionStorage.removeItem('selectedCategory');
    }

    return () => {
      window.removeEventListener('categoryChange', handleExternalCategoryChange);
    };
  }, []);

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await fetch(API_URL, { 
          method: "GET",
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const itemsData = await response.json();

        if (itemsData.data && Array.isArray(itemsData.data)) {
          dispatch(setItems(itemsData.data));
        } else {
          console.error("Unexpected API response structure:", itemsData);
          dispatch(setItems([]));
        }
      } catch (error) {
        console.error("Error fetching items:", error);
        dispatch(setItems([]));
      }
    };

    fetchItems();
  }, [dispatch]);

  return (
    <Box 
      component="section"
      id="shopping-list"
      sx={{
        width: "100%",
        minHeight: "100vh",
        py: { xs: 8, md: 12 },
        px: { xs: 2, sm: 4, md: 6 },
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      {/* Header */}
      <Typography 
        variant="h3"
        component="h2"
        textAlign="center"
        sx={{ 
          mb: 3,
          fontSize: { xs: "2rem", md: "3rem" },
          fontWeight: 600,
        }}
      >
        Our Featured <Box component="span" sx={{ color: "primary.main" }}>Albums</Box>
      </Typography>

      {/* Search */}
      <Input
        value={searchTerm}
        onChange={handleSearchChange}
        placeholder="Search albums..."
        disableUnderline
        sx={{
          width: "100%",
          maxWidth: "500px",
          mb: 3,
          px: 2,
          py: 1,
          border: "1px solid",
          borderColor: "divider",
          borderRadius: 1,
          fontSize: "0.95rem",
          transition: "border-color 0.2s ease",
          "&:hover": {
            borderColor: "text.secondary",
          },
          "&:focus-within": {
            borderColor: "primary.main",
          },
        }}
      />

      {/* Tabs */}
      <Tabs
        value={selectedCategory}
        onChange={handleCategoryChange}
        textColor="primary"
        indicatorColor="primary"
        variant="scrollable"
        scrollButtons="auto"
        sx={{
          mb: 6,
          "& .MuiTabs-indicator": {
            height: 2,
          },
          "& .MuiTab-root": {
            minWidth: { xs: 80, sm: 100 },
            fontSize: "0.875rem",
            fontWeight: 500,
            letterSpacing: "0.5px",
            py: 1.5,
            px: 2,
          },
        }}
      >
        {CATEGORIES.map((category) => (
          <Tab 
            key={category.value}
            label={category.label}
            value={category.value}
          />
        ))}
      </Tabs>

      {/* Grid */}
      <Box
        sx={{
          width: "100%",
          maxWidth: "1600px",
          display: "grid",
          gridTemplateColumns: {
            xs: "1fr",
            sm: "repeat(2, 1fr)",
            lg: "repeat(3, 1fr)",
          },
          gap: { xs: 4, md: 5, lg: 6 },
          justifyItems: "center",
        }}
      >
        {filteredItems.length === 0 ? (
          <Box 
            sx={{ 
              gridColumn: { xs: "1", sm: "1 / -1" },
              textAlign: "center",
              py: 8,
            }}
          >
            <Typography variant="h6" color="text.secondary">
              {searchTerm ? "No albums found" : "Loading albums..."}
            </Typography>
          </Box>
        ) : (
          filteredItems.map((item) => (
            <Box
              key={`item-${item.id}`}
              sx={{
                width: "100%",
                maxWidth: "480px",
                transition: "transform 0.3s cubic-bezier(0.34, 1.56, 0.64, 1)",
                "&:hover": {
                  transform: "scale(1.05) translateY(-8px)",
                },
              }}
            >
              <Item item={item} />
            </Box>
          ))
        )}
      </Box>

      {/* Count */}
      {filteredItems.length > 0 && (
        <Typography 
          variant="body2" 
          color="text.secondary" 
          textAlign="center"
          sx={{ mt: 6 }}
        >
          {filteredItems.length} albums
          {searchTerm && ` 路 "${searchTerm}"`}
          {selectedCategory !== 'all' && ` 路 ${selectedCategory}`}
        </Typography>
      )}
    </Box>
  );
};

export default ShoppingList;
