import React, { useEffect, useState } from "react";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Box from "@mui/material/Box";
import Item from "../../components/Item";
import { Typography, Input } from "@mui/material";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useDispatch, useSelector } from "react-redux";
import { setItems } from "../../state";

const ShoppingList = () => {
  const dispatch = useDispatch();
  const [value, setValue] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const items = useSelector((state) => state.cart.items);
  const isNonMobile = useMediaQuery("(min-width:600px)");

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  useEffect(() => {
    async function getItems() {
      try {
        const response = await fetch(
          "http://localhost:1337/api/items?populate=image&pagination[limit]=50",
          { method: "GET" }
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const itemsJson = await response.json();

        if (itemsJson.data && Array.isArray(itemsJson.data)) {
          dispatch(setItems(itemsJson.data));
        } else {
          console.error("Unexpected data structure:", itemsJson);
          dispatch(setItems([]));
        }
      } catch (error) {
        console.error("Error fetching items:", error);
        dispatch(setItems([]));
      }
    }
    getItems();
  }, [dispatch]);

  return (
    <Box width="80%" margin="80px auto">
      <Typography variant="h3" textAlign="center">
        Our Top Picks <b>Albums</b>
      </Typography>
      <Input
        value={searchTerm}
        onChange={handleSearch}
        placeholder="Search..."
        fullWidth
        sx={{
          margin: "10px 0",
          transition: "transform 0.3s ease-in-out",
          "&:focus-within": {
            transform: "translateX(0)",
          },
          transform: searchTerm ? "translateX(0)" : "translateX(-50px)",
        }}
      />
      <Tabs
        textColor="primary"
        indicatorColor="primary"
        value={value}
        onChange={handleChange}
        centered
        TabIndicatorProps={{ sx: { display: isNonMobile ? "block" : "none" } }}
        sx={{
          m: "25px",
          "& .MuiTabs-flexContainer": {
            flexWrap: "wrap",
          },
        }}
      >
        <Tab label="ALL" value="all" />
        <Tab label="JAZZ" value="jazz" />
        <Tab label="POP" value="pop" />
        <Tab label="ROCK" value="rock" />
        <Tab label="SOUL" value="soul" />
      </Tabs>
      <Box
        margin="0 auto"
        display="grid"
        gridTemplateColumns={{
          xs: "1fr",
          sm: "repeat(auto-fill, minmax(300px, 1fr))",
          md: "repeat(auto-fill, minmax(400px, 1fr))",
        }}
        justifyContent="space-around"
        rowGap="20px"
        columnGap="1%"
        p={1}
      >
        {items
          ?.filter(
            (item) =>
              value === "all" ||
              item.attributes?.category?.toLowerCase() === value
          )
          ?.filter(
            (item) =>
              searchTerm.trim() === "" ||
              item.attributes?.name?.toLowerCase().includes(searchTerm.toLowerCase())
          )
          ?.map((item) => (
            <Item
              item={item}
              key={`${item.attributes?.name}-${item.id}`}
              style={{ maxWidth: "100%" }}
            />
          ))}
      </Box>
    </Box>
  );
};

export default ShoppingList;
