import React, { useEffect, useState, useMemo, useCallback } from "react";
import { 
  Tabs, 
  Tab, 
  Box, 
  Typography, 
  Input,
  useMediaQuery
} from "@mui/material";
import { useDispatch, useSelector } from "react-redux";
import Item from "../../components/Item";
import { setItems } from "../../state";

// Constants
const API_URL = "https://sprint9-ecommerce-production.up.railway.app/api/items?populate=image&pagination[limit]=50";
const CATEGORIES = [
  { label: "ALL", value: "all" },
  { label: "JAZZ", value: "jazz" },
  { label: "POP", value: "pop" },
  { label: "ROCK", value: "rock" },
  { label: "SOUL", value: "soul" },
];

const ShoppingList = () => {
  const dispatch = useDispatch();

  // Media queries for responsive design
  const isSmallMobile = useMediaQuery("(max-width:480px)");
  const isMobile = useMediaQuery("(max-width:768px)");
  const isTablet = useMediaQuery("(max-width:1024px)");

  const [selectedCategory, setSelectedCategory] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const items = useSelector((state) => state.cart.items);

  // Calculate columns based on screen size - FIXED for no horizontal scroll
  const getGridColumns = useCallback(() => {
    if (isSmallMobile) return "minmax(280px, 1fr)";
    if (isMobile) return "minmax(300px, 1fr)";
    if (isTablet) return "repeat(auto-fit, minmax(300px, 1fr))";
    // For desktop, use auto-fit with proper minmax to prevent overflow
    return "repeat(auto-fit, minmax(320px, 1fr))";
  }, [isSmallMobile, isMobile, isTablet]);

  // Calculate max container width to prevent overflow
  const getContainerWidth = useCallback(() => {
    if (isSmallMobile) return "95%";
    if (isMobile) return "90%";
    if (isTablet) return "85%";
    // For desktop, limit max width to prevent items from getting too spread out
    return "min(90%, 1400px)"; // Max 1400px to maintain good proportions
  }, [isSmallMobile, isMobile, isTablet]);

  // Calculate item max width based on screen size
  const getItemMaxWidth = useCallback(() => {
    if (isSmallMobile) return "100%";
    if (isMobile) return "100%";
    if (isTablet) return "400px";
    return "380px"; // Slightly smaller for 3-column layout
  }, [isSmallMobile, isMobile, isTablet]);

  // Event handlers
  const handleCategoryChange = useCallback((event, newValue) => {
    setSelectedCategory(newValue);
  }, []);

  const handleSearchChange = useCallback((event) => {
    setSearchTerm(event.target.value);
  }, []);

  // Filter items based on category and search term
  const filteredItems = useMemo(() => {
    return items.filter(item => {
      const matchesCategory = selectedCategory === "all" || 
        item.attributes?.category?.toLowerCase() === selectedCategory;

      const matchesSearch = searchTerm.trim() === "" ||
        item.attributes?.name?.toLowerCase().includes(searchTerm.toLowerCase());

      return matchesCategory && matchesSearch;
    });
  }, [items, selectedCategory, searchTerm]);

  // Listen for category changes from MainCarousel
  useEffect(() => {
    const handleExternalCategoryChange = (event) => {
      setSelectedCategory(event.detail.category);
    };

    window.addEventListener('categoryChange', handleExternalCategoryChange);

    const savedCategory = sessionStorage.getItem('selectedCategory');
    if (savedCategory && CATEGORIES.some(cat => cat.value === savedCategory)) {
      setSelectedCategory(savedCategory);
      sessionStorage.removeItem('selectedCategory');
    }

    return () => {
      window.removeEventListener('categoryChange', handleExternalCategoryChange);
    };
  }, []);

  // Fetch items from API
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await fetch(API_URL, { 
          method: "GET",
          headers: {
            'Content-Type': 'application/json',
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const itemsData = await response.json();

        if (itemsData.data && Array.isArray(itemsData.data)) {
          dispatch(setItems(itemsData.data));
        } else {
          console.error("Unexpected API response structure:", itemsData);
          dispatch(setItems([]));
        }
      } catch (error) {
        console.error("Error fetching items:", error);
        dispatch(setItems([]));
      }
    };

    fetchItems();
  }, [dispatch]);

  // Render loading state
  const renderContent = () => {
    if (items.length === 0) {
      return (
        <Box 
          display="flex" 
          justifyContent="center" 
          alignItems="center" 
          minHeight="200px"
          width="100%"
        >
          <Typography 
            variant="h6" 
            textAlign="center" 
            color="text.secondary"
          >
            Loading albums...
          </Typography>
        </Box>
      );
    }

    if (filteredItems.length === 0) {
      return (
        <Box 
          display="flex" 
          justifyContent="center" 
          alignItems="center" 
          minHeight="200px"
          width="100%"
        >
          <Typography 
            variant="h6" 
            textAlign="center" 
            color="text.secondary"
          >
            {searchTerm 
              ? `No albums found for "${searchTerm}" in ${selectedCategory === 'all' ? 'any category' : selectedCategory}`
              : `No albums found in ${selectedCategory} category`
            }
          </Typography>
        </Box>
      );
    }

    return (
      <Box
        display="grid"
        gridTemplateColumns={getGridColumns()}
        gap="24px"
        justifyContent="center"
        justifyItems="center"
        width="100%"
        maxWidth="100%"
        margin="0 auto"
        padding="24px 0"
        sx={{
          // Ensure no horizontal overflow
          overflow: "hidden",
          boxSizing: "border-box"
        }}
      >
        {filteredItems.map((item) => (
          <Box
            key={`item-${item.id}-${item.attributes?.name}`}
            sx={{
              width: "100%",
              display: "flex",
              justifyContent: "center",
              maxWidth: getItemMaxWidth()
            }}
          >
            <Item
              item={item}
              style={{ 
                width: "100%",
                height: "100%"
              }}
            />
          </Box>
        ))}
      </Box>
    );
  };

  return (
    <Box 
      component="section"
      aria-label="Music albums collection"
      width={getContainerWidth()}
      margin={{ xs: "40px auto", sm: "60px auto", md: "80px auto" }}
      id="shopping-list"
      display="flex"
      flexDirection="column"
      alignItems="center"
      sx={{
        // Prevent any horizontal overflow
        overflow: "hidden",
        boxSizing: "border-box",
        maxWidth: "100vw"
      }}
    >
      {/* Header */}
      <Typography 
        variant={{ xs: "h4", sm: "h3" }}
        component="h2"
        textAlign="center"
        sx={{ 
          mb: 4,
          fontWeight: 600,
          width: "100%",
          px: 2 // Add horizontal padding to prevent text touching edges
        }}
      >
        Our Featured <Box component="span" sx={{ color: "primary.main", fontWeight: 700 }}>Albums</Box>
      </Typography>

      {/* Search Input */}
      <Box 
        width="100%" 
        display="flex" 
        justifyContent="center"
        mb={3}
        px={2} // Add horizontal padding
      >
        <Input
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search albums by name..."
          sx={{
            width: { xs: "100%", sm: "80%", md: "70%", lg: "60%" },
            maxWidth: "500px",
            transition: "all 0.3s ease-in-out",
            '&:focus-within': {
              transform: "scale(1.02)",
            },
          }}
          aria-label="Search albums"
        />
      </Box>

      {/* Category Tabs */}
      <Box 
        width="100%" 
        display="flex" 
        justifyContent="center"
        mb={4}
        px={2} // Add horizontal padding
      >
        <Tabs
          value={selectedCategory}
          onChange={handleCategoryChange}
          textColor="primary"
          indicatorColor="primary"
          centered
          variant={isMobile ? "scrollable" : "standard"}
          scrollButtons={isMobile ? "auto" : false}
          sx={{
            width: { xs: "100%", sm: "auto" },
            maxWidth: "100%",
            '& .MuiTabs-flexContainer': {
              justifyContent: "center",
            },
            '& .MuiTab-root': {
              fontSize: { xs: "0.75rem", sm: "0.85rem", md: "0.9rem" },
              minWidth: { xs: "60px", sm: "80px" },
              px: { xs: 1.5, sm: 2, md: 3 },
              mx: { xs: 0.5, sm: 1 },
            },
          }}
        >
          {CATEGORIES.map((category) => (
            <Tab 
              key={category.value}
              label={category.label}
              value={category.value}
              aria-label={`Show ${category.label.toLowerCase()} albums`}
            />
          ))}
        </Tabs>
      </Box>

      {/* Items Grid */}
      {renderContent()}

      {/* Results Count */}
      {filteredItems.length > 0 && (
        <Typography 
          variant="body2" 
          color="text.secondary" 
          textAlign="center"
          sx={{ mt: 4, width: "100%", px: 2 }}
        >
          Showing {filteredItems.length} of {items.length} albums
          {searchTerm && ` for "${searchTerm}"`}
          {selectedCategory !== 'all' && ` in ${selectedCategory}`}
        </Typography>
      )}
    </Box>
  );
};

export default ShoppingList;
