import { Box, Button, IconButton, Typography } from "@mui/material";
import { useSelector, useDispatch } from "react-redux";
import CloseIcon from "@mui/icons-material/Close";
import styled from "@emotion/styled";
import { shades } from "../../theme";
import { removeFromCart, setIsCartOpen } from "../../state";
import { useNavigate } from "react-router-dom";

const FlexBox = styled(Box)`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CartMenu = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const isCartOpen = useSelector((state) => state.cart.isCartOpen);

  // Log full cart data for debugging
  console.log("Cart data:", JSON.stringify(cart, null, 2));

  const totalPrice = cart.reduce((total, item) => {
    const price = item?.attributes?.price || 0;
    const count = item?.count || 0;
    console.log(`Item ${item?.attributes?.name || "unknown"}: price=${price}, count=${count}`);
    return total + count * price;
  }, 0);

  // Get image URL with debugging
  const getImageUrl = (item) => {
    const imagePath = item?.attributes?.image?.data?.attributes?.formats?.medium?.url;
    const imageUrl = imagePath
      ? `http://localhost:1337${imagePath}`
      : "/default-image.jpg";
    console.log(`Image URL for ${item?.attributes?.name || "item"}: ${imageUrl}`);
    return imageUrl;
  };

  return (
    <Box
      display={isCartOpen ? "block" : "none"}
      backgroundColor="rgba(0, 0, 0, 0.4)"
      position="fixed"
      zIndex={10}
      width="100%"
      height="100%"
      left="0"
      top="0"
      overflow="auto"
    >
      <Box
        position="fixed"
        right="0"
        bottom="0"
        width={{ xs: "90%", sm: "max(400px, 30%)" }}
        height="100%"
        backgroundColor="white"
      >
        <Box padding="20px" overflow="auto" height="100%">
          {/* HEADER */}
          <FlexBox mb="10px">
            <Typography variant="h4">SHOPPING BAG ({cart?.length || 0})</Typography>
            <IconButton
              onClick={() => dispatch(setIsCartOpen({}))}
              aria-label="Close cart"
            >
              <CloseIcon />
            </IconButton>
          </FlexBox>

          {/* CART LIST */}
          <Box>
            {cart?.length ? (
              cart.map((item) => (
                <Box key={`${item?.attributes?.name || "item"}-${item?.id || Math.random()}`}>
                  <FlexBox p="10px 0" alignItems="center">
                    <Box flex="0 0 100px">
                      <img
                        alt={item?.attributes?.name || "Item"}
                        width="100px"
                        height="100px"
                        src={getImageUrl(item)}
                        style={{ objectFit: "cover", border: "1px solid #ddd" }}
                        loading="lazy"
                        onError={(e) => {
                          console.error(`Image failed to load for ${item?.attributes?.name || "item"}: ${e.target.src}`);
                          e.target.src = "/default-image.jpg";
                        }}
                      />
                    </Box>
                    <Box flex="1" ml="10px">
                      <FlexBox mb="5px">
                        <Typography fontWeight="bold" fontSize="16px">
                          {item?.attributes?.name || "Unnamed Item"}
                        </Typography>
                        <IconButton
                          onClick={() => dispatch(removeFromCart({ id: item?.id }))}
                          aria-label={`Remove ${item?.attributes?.name || "item"}`}
                        >
                          <CloseIcon />
                        </IconButton>
                      </FlexBox>
                      <FlexBox>
                        <Typography fontSize="14px">Qty: {item?.count || 1}</Typography>
                        <Typography fontWeight="bold" fontSize="14px" ml="10px">
                          €{item?.attributes?.price?.toFixed(2) || "0.00"}
                        </Typography>
                      </FlexBox>
                    </Box>
                  </FlexBox>
                </Box>
              ))
            ) : (
              <Typography textAlign="center" m="20px 0">
                Your cart is empty
              </Typography>
            )}
          </Box>

          {/* SUBTOTAL AND CHECKOUT */}
          <Box m="20px 0">
            <FlexBox m="10px 0">
              <Typography fontWeight="bold">SUBTOTAL</Typography>
              <Typography fontWeight="bold">€{totalPrice.toFixed(2)}</Typography>
            </FlexBox>
            <Button
              sx={{
                backgroundColor: shades.primary[400],
                color: "white",
                borderRadius: 0,
                minWidth: "100%",
                padding: "10px 20px",
                m: "10px 0",
              }}
              onClick={() => {
                navigate("/checkout");
                dispatch(setIsCartOpen({}));
              }}
              disabled={!cart?.length}
              aria-label="Proceed to checkout"
            >
              CHECKOUT
            </Button>
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default CartMenu;
