import { useTheme } from "@emotion/react";
import { Box, Typography } from "@mui/material";
import { shades } from "../../theme";
import Logo from "../../assets/logo/logo-imagen.png"; // Importa la imagen del logo

function Footer() {
  const {
    palette: { primary },
  } = useTheme();
  return (
    <Box marginTop="70px" padding="40px 0" backgroundColor={primary.main}>
      <Box
        width="80%"
        margin="auto"
        display="grid"
        gridTemplateColumns="1fr 1fr" // Divide en dos columnas
        color="#FFC709"
        columnGap="clamp(20px, 30px, 40px)" // Espacio entre las columnas
      >
        {/* Lado Izquierdo */}
        <Box>
          <img src={Logo} alt="Logo" style={{ maxWidth: "100%" }} />
        </Box>

        {/* Lado Derecho */}
        <Box>
          <Typography variant="h4" fontWeight="bold" mb="30px">
            About Us
          </Typography>
          <Typography mb="30px">Contact Us</Typography>
          <Typography mb="30px">Grading</Typography>
          <Typography mb="30px">Shipping Terms</Typography>

          {/* Agrega tus redes sociales aqu√≠ */}
        </Box>
      </Box>
    </Box>
  );
}

export default Footer;
