import React, { useState, useCallback, useMemo } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, Typography } from "@mui/material";
import { addToCart, removeFromCart } from "../state";
import { useNavigate } from "react-router-dom";

const Item = ({ item, style }) => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart.cart);
  const [imageLoaded, setImageLoaded] = useState(false);

  // Memoized values and handlers
  const handleAddToCart = useCallback((e) => {
    e.stopPropagation();
    if (item?.attributes) {
      const isItemInCart = cart.some((cartItem) => cartItem.id === item.id);
      if (!isItemInCart) {
        dispatch(addToCart({ item: { ...item, count: 1 } }));
      } else {
        dispatch(removeFromCart({ id: item.id }));
      }
    }
  }, [cart, dispatch, item]);

  const handleNavigate = useCallback(() => {
    if (item?.id) {
      navigate(`/item/${item.id}`);
    }
  }, [navigate, item]);

  // Extract and memoize item data
  const { itemData, isItemInCart, imageUrl } = useMemo(() => {
    if (!item?.attributes) {
      return { itemData: null, isItemInCart: false, imageUrl: "" };
    }

    const { category = "Unknown", price = 0, name = "Unnamed Item", image } = item.attributes;

    const imageUrl = image?.data?.attributes?.formats?.medium?.url 
      ? `https://sprint9-ecommerce-production.up.railway.app${image.data.attributes.formats.medium.url}`
      : "/default-image.jpg";

    const isInCart = cart.some((cartItem) => cartItem.id === item.id);

    return {
      itemData: { category, price, name },
      isItemInCart: isInCart,
      imageUrl
    };
  }, [item, cart]);

  // Loading/Error placeholder
  if (!item?.attributes) {
    return (
      <Box 
        sx={{ 
          maxWidth: { xs: '100%', sm: '400px' }, 
          margin: '0 auto',
          ...style 
        }}
      >
        <Box
          sx={{
            backgroundColor: '#f8f8f8',
            aspectRatio: '1 / 1',
            width: '100%',
          }}
        />
      </Box>
    );
  }

  const { category, price, name } = itemData;

  // Premium color palette
  const colors = {
    text: '#1a1a1a',
    background: '#ffffff',
    accent: '#1a1a1a',
    border: '#e8e8e8',
    inCart: '#1a1a1a'
  };

  const styles = {
    container: {
      maxWidth: { xs: '100%', sm: '400px' },
      margin: '0 auto',
      cursor: 'pointer',
      backgroundColor: colors.background,
      position: 'relative',
      '&::before': isItemInCart ? {
        content: '""',
        position: 'absolute',
        top: '-1px',
        left: '-1px',
        right: '-1px',
        bottom: '-1px',
        border: '1px solid #1a1a1a',
        zIndex: 1,
        pointerEvents: 'none',
      } : {},
      ...style
    },
    imageContainer: {
      aspectRatio: '1 / 1',
      backgroundColor: '#f8f8f8',
      marginBottom: '20px',
      overflow: 'hidden',
      position: 'relative',
      '&::after': isItemInCart ? {
        content: '"IN COLLECTION"',
        position: 'absolute',
        top: '12px',
        right: '12px',
        fontSize: '0.6rem',
        fontWeight: 400,
        letterSpacing: '0.5px',
        color: '#1a1a1a',
        fontFamily: 'system-ui, sans-serif',
      } : {},
    },
    image: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
      opacity: imageLoaded ? 1 : 0,
      transition: 'opacity 0.8s ease',
    },
    category: {
      fontSize: '0.65rem',
      fontWeight: 400,
      color: '#666',
      letterSpacing: '0.8px',
      textTransform: 'uppercase',
      marginBottom: '6px',
      fontFamily: 'system-ui, sans-serif',
    },
    name: {
      fontSize: '0.95rem',
      fontWeight: 400,
      color: colors.text,
      lineHeight: 1.4,
      marginBottom: '12px',
      fontFamily: 'system-ui, sans-serif',
      letterSpacing: '-0.2px',
    },
    priceSection: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      borderTop: '1px solid #f0f0f0',
      paddingTop: '12px',
    },
    price: {
      fontSize: '0.9rem',
      fontWeight: 400,
      color: colors.text,
      fontFamily: 'system-ui, sans-serif',
      letterSpacing: '-0.2px',
    },
    cartAction: {
      color: isItemInCart ? '#666' : colors.text,
      fontSize: '0.7rem',
      fontWeight: 400,
      textTransform: 'uppercase',
      letterSpacing: '0.5px',
      padding: 0,
      border: 'none',
      backgroundColor: 'transparent',
      cursor: 'pointer',
      transition: 'color 0.2s ease',
      fontFamily: 'system-ui, sans-serif',
      '&:hover': {
        color: isItemInCart ? '#333' : '#666',
      },
    }
  };

  return (
    <Box
      onClick={handleNavigate}
      sx={styles.container}
    >
      {/* Image */}
      <Box sx={styles.imageContainer}>
        <Box
          component="img"
          alt={name}
          src={imageUrl}
          onLoad={() => setImageLoaded(true)}
          sx={styles.image}
        />
      </Box>

      {/* Info */}
      <Box>
        <Typography sx={styles.category}>
          {category}
        </Typography>

        <Typography sx={styles.name}>
          {name}
        </Typography>

        <Box sx={styles.priceSection}>
          <Typography sx={styles.price}>
            â‚¬{price.toFixed(2)}
          </Typography>

          <Box 
            component="button"
            onClick={handleAddToCart}
            sx={styles.cartAction}
          >
            {isItemInCart ? 'Remove' : 'Add to Cart'}
          </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default Item;
