import React from "react";
import { Box, Typography, Grid } from "@mui/material";

const AboutUs = () => {
  return (
    <Box width="80%" m="80px auto">
      <Grid container spacing={6} alignItems="center">
        {/* Foto personal */}
        <Grid
          item
          xs={12}
          md={5}
          lg={6}
          sx={{
            display: "flex",
            justifyContent: "center",
          }}
        >
          <Box
            component="img"
            alt="Pablo portrait"
            src="/images/aboutus-photo.jpg"
            sx={{
              width: "100%",
              height: "auto",
              objectFit: "cover",
              borderRadius: "8px",
              maxWidth: { lg: "700px", xs: "100%" }, // límite en pantallas grandes
            }}
          />
        </Grid>

        {/* Biografía */}
        <Grid item xs={12} md={7} lg={6}>
          <Typography variant="h3" fontWeight="bold" gutterBottom>
            About Us
          </Typography>
          <Typography variant="body1" sx={{ mt: 2, lineHeight: 1.6 }}>
            Hi, I’m Pablo, the founder and driving force behind this record
            store project. Music has always been at the core of my life —
            from years as a sound designer and collector, to building a
            community through Allmyrecords & Discogs. Over time, I’ve combined
            this passion with technology, transitioning into web development
            and front-end design.
          </Typography>
          <Typography variant="body1" sx={{ mt: 2, lineHeight: 1.6 }}>
            This project is more than just an e-commerce demo — it’s a space
            where my two worlds meet: the love for vinyl records and the
            creative possibilities of coding. Every detail you see here is
            guided by the idea of making music discovery and collecting
            meaningful, simple, and inspiring.
          </Typography>
          <Typography variant="body1" sx={{ mt: 2, lineHeight: 1.6 }}>
            Thanks for stopping by and sharing a piece of this journey. Whether
            you’re a passionate collector or just curious about records, I hope
            you enjoy exploring this project as much as I enjoyed creating it.
          </Typography>
        </Grid>
      </Grid>
    </Box>
  );
};

export default AboutUs;
