import { useTheme } from "@emotion/react";
import { Box, Typography } from "@mui/material";
import Logo from "../../assets/logo/logo-imagen.png";
import TwitterIcon from "@mui/icons-material/Twitter";
import InstagramIcon from "@mui/icons-material/Instagram";

function Footer() {
  const {
    palette: { primary },
  } = useTheme();
  return (
    <Box marginTop="70px" padding="40px 0" backgroundColor={primary.main}>
      <Box
        width="80%"
        margin="auto"
        display="grid"
        gridTemplateColumns="1fr 1fr" // Divide en dos columnas
        color="#FFC709"
        columnGap="clamp(20px, 30px, 40px)" // Espacio entre las columnas
      >
        <Box>
          <img src={Logo} alt="Logo" style={{ maxWidth: "50%" }} />
        </Box>
        <Box display="flex" flexDirection="column" alignItems="flex-end">
          <Typography variant="h4" fontWeight="bold" fontSize="1.2em">
            About Us
          </Typography>
          <Typography fontSize="1.2em">Contact Us</Typography>
          <Typography fontSize="1.2em">Grading</Typography>
          <Typography fontSize="1.2em">Shipping Terms</Typography>

          <Box display="flex">
            <Box display="flex" alignItems="center" mr={2}>
              <TwitterIcon sx={{ fontSize: 30 }} />
            </Box>
            <Box display="flex" alignItems="center">
              <InstagramIcon sx={{ fontSize: 30 }} />
            </Box>
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default Footer;
